INSERT INTO `l_product` VALUES (1, '苜草素1号 10Kg装 粉状 按1%预混', '蓝海洋科技', 0.00, '/public/uploads/product/1.jpg', '/public/uploads/product/thumb/1.jpg', '苜草素 10kg 检测 标准 检测报告', '<p><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;"><br/></span></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;"><img src="http://www.mucaosu.com/public/uploads/product/1_1.jpg"alt=""width="800"height="1206"title=""align=""/><br/></span></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;"><img src="http://www.mucaosu.com/public/uploads/product/1_2.jpg"alt=""width="800"height="1191"title=""align=""/><br/></span></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;">苜草素1号产品说明书<span></span></span></p><hr/><p class="MsoNormal"style="text-indent:21.1pt;"><b><span style="line-height:150%;font-family:黑体;">&nbsp;</span></b><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">是一种以紫花苜蓿为主要原料的天然植物提取物，具有增强动物免疫机能、改善畜产品质量、促进畜禽生长的作用，综合效果可替代饲用抗生素，而且无毒副作用，是一种新型的绿色的有效保护畜禽健康的饲料添加剂。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要成份</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">的有效活性成分包括：</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">多糖≥<span>15%</span>、</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">黄酮</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">≥<span>5%</span>、皂甙≥<span>5%</span>。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">产品性状<span>&nbsp;</span></span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">棕黄色粉末，气味芳香。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要功能<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、提高母源抗体水平，增强幼龄动物的抵抗力。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;2</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、增强动物机体免疫力，促进生长。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;3</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、调节脂肪代谢，有效预防脂肪肝等营养代谢病。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、改善肉质和风味。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;5</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、替代饲用抗生素。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;<span style="color:blue;">&nbsp;6</span></span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:blue;">、通过苜蓿黄酮中类雌激素的双向调节作用，提高生猪产子数和蛋鸡产蛋率。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">作用机理<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">苜草素中的多糖、黄酮和皂苷等有效成分可以促进动物免疫器官的发育，增强<span>T</span>、<span>B</span>淋巴细胞的活化，提高血清抗体水平，从而显著增强机体的免疫功能，提高母源抗体水平。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">另外，苜草素可以降低脂肪合成酶和脂蛋白酯酶的活性，反而提高脂蛋白酯酶和解偶联蛋白的活性，从而改善了机体的脂肪代谢过程，降低了脂肪沉积，促进了肌肉发育，改善了畜产品质量和风味。<span>`</span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;">&nbsp;&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;font-family:宋体;">应用效果</span></b><span style="font-size:12.0pt;"></span></p><p class="MsoNormal"align="left"style="text-indent:24pt;"><span style="font-size:12pt;line-height:150%;font-family:宋体;">1</span><span style="font-size:12pt;line-height:150%;font-family:宋体;">、调节畜禽机体免疫功能，提高畜禽自身的抗病力，增强抗逆性，预防幼畜禽腹泻，综合效果可替代饲用抗生素。<span></span></span></p><p class="MsoNormal"align="left"style="text-indent:24pt;"><span style="font-size:12pt;line-height:150%;font-family:宋体;">2</span><span style="font-size:12pt;line-height:150%;font-family:宋体;">、苜草素可分别提高猪和肉鸡体增重<span>15%</span>和<span>7%</span>，提高猪和肉鸡的饲料转化率<span>15%</span>和<span>3%</span>；提高蛋鸡产蛋率<span>4%</span>，降低死淘率<span>22%</span>；提高母猪产仔率<span>10%</span>，提高仔猪成活率<span>6%</span>。<span></span></span></p><p class="MsoNormal"align="left"style="text-indent:24pt;"><span style="font-size:12pt;line-height:150%;font-family:宋体;">3</span><span style="font-size:12pt;line-height:150%;font-family:宋体;">、调节脂肪代谢、减少腹脂沉积，提高肉鸡胸肌率<span>7%</span>，降低腹脂率<span>15%</span>，降低鸡蛋胆固醇含量，改善肉质和风味。<span></span></span></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;">使用方法</span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:red;">1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:red;">、苜草素产品分为原粉和预混剂两种。原粉吸潮性强，在使用前应先作预混处理，以避免吸潮结块。预混剂可直接使用，但应注意苜草素的含量。<span></span></span></p><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:red;">2</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:red;">、苜草素产品应在通风阴凉处保存，开封后注意防潮。<span></span></span></p><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:red;">3</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:red;">、在配合饲料中的推荐用量及使用方法。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">猪配合饲料</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p><table class="MsoNormalTable"border="1"cellspacing="0"cellpadding="0"style="border-collapse:collapse;border:none;"><tbody><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">使用对象<span></span></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">添加量（公斤<span>/</span>吨）<span></span></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">乳猪（断奶前）</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.15~0.2</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">仔猪（断奶</span><span>-20</span><span style="font-family:宋体;">公斤）</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.6~0.7</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">育肥猪（</span><span>20</span><span style="font-family:宋体;">公斤</span><span>-</span><span style="font-family:宋体;">屠宰）</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.3~0.5</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">母猪</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.4~0.5</span></p></td></tr></tbody></table><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">鸡配合饲料</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p><table class="MsoNormalTable"border="1"cellspacing="0"cellpadding="0"style="border-collapse:collapse;border:none;"><tbody><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">使用对象<span></span></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">添加量（公斤<span>/</span>吨）<span></span></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">雏鸡（</span><span>0-21</span><span style="font-family:宋体;">天，包括肉鸡和蛋鸡）</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.15~0.2</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">肉鸡（</span><span>21-49</span><span style="font-family:宋体;">天）</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.5~0.6</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">育成鸡（</span><span>21-110</span><span style="font-family:宋体;">天）</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.1~0.15</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">产蛋鸡（</span><span>110-500</span><span style="font-family:宋体;">天）</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.15~0.2</span></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">种鸡（收集种蛋期间）</span></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.4~0.5</span></p></td></tr></tbody></table><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:red;"></span></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;">保质期为<span>2</span>年。</span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p><br/></p><p><br/></p><p><span style="color:#006600;font-size:24px;">中国农业科学畜牧研究所检测报告</span></p><hr/><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;无公害饲料添加剂－"苜草素"是由中国农科院畜牧研究所研制，于2003年12月被批准为饲料添加剂新产品，新产品证书号为新饲证字(2003)06号。"苜草素"是一个具有我国自主知识产权的无公害天然饲料添加剂新产品，目前已经获得国家发明专利。</span></p><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过实践证明，在肉仔鸡饲养过程中添加"苜草素"，可以提高肉仔鸡平均日增重11.92%，提高饲料转化效率2.65%，降低腹脂14.7%，提高胸肌率8.5%，能够减少早期肉仔鸡腹泻，提高存活率9.3%；可以抑制肉仔鸡不同生长阶段肠道内大肠杆菌的繁殖，降低菌群数量。在同等饲养条件下，添加"苜草素"仔猪平均日增重提高5.5%，饲料转化效率提高1.3%，有效的提高了猪肉产品的质量，保持了猪肉产品天然的风味和口感。</span></p><p><span style="font-size:16px;">检测报告：</span></p><p><span style="font-size:16px;">中国医学科学院药用植物研究所</span><span style="line-height:1.5;font-size:16px;">检测报告</span></p><p><span style="line-height:1.5;font-size:16px;"><b><span style="font-size:16px;">1.</span></b><b><span style="font-size:16px;">样品基本信息</span></b><br/></span></p><p class="MsoNormal"style="font-family: tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检单位：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">中国农业科学院畜牧研究所</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family: tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测样品：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family: tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">样品数量：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;4</span></u><u><span style="font-size:14px;">份</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="font-family: tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检日期：</span><u><span style="font-size:14px;">&nbsp;2014</span></u><u><span style="font-size:14px;">年</span></u><u><span style="font-size:14px;">7</span></u><u><span style="font-size:14px;">月</span></u><u><span style="font-size:14px;">15</span></u><u><span style="font-size:14px;">日</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><u></u></p><p class="MsoNormal"style="font-family: tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测内容：</span><u><span style="font-size:14px;">&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素样品中总皂苷的含量检测</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><u></u></p><p class="MsoNormal"style="font-family: tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">编号方法：</span><u><span style="font-size:14px;">&nbsp;4</span></u><u><span style="font-size:14px;">份送检样品分别编号为</span></u><u><span style="font-size:14px;">1-4</span></u><u><span style="font-size:14px;">，见图</span></u><u><span style="font-size:14px;">1&nbsp;</span></u></p><p class="MsoNormal"style="font-family: tahoma, verdana, helvetica;text-align:justify;"><u><span style="font-size:14px;"><br/></span></u></p><p><!--[if gte vml 1]><![endif]--><!--[if!vml]--><img width="467"height="192"src="/Public/Uploads/image/201507/8typhzh.jpg"title="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"align=""alt="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"/></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">2.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测目标</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">利用分光光度法测定送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">3.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测方法</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:16px;line-height:150%;">3.1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">检测波长的确定</span></p><p class="MsoNormal"style="margin-left:18.0pt;text-align:justify;text-indent:-18.0pt;"><b><span style="font-size:12.0pt;line-height:150%;"><span style="font-size:16px;">4.</span><span style="font-weight:normal;font-size:16px;line-height:normal;">&nbsp;</span></span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测结果</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量在</span><span style="font-size:16px;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:16px;line-height:150%;font-family:宋体;">之间，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品含量最高，</span><span style="font-size:16px;line-height:150%;">1-3</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品的总皂苷含量接近，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份样品间差异均不显著（表</span><span style="font-size:16px;line-height:150%;">1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">，</span><i><span style="font-size:16px;line-height:150%;">P</span></i><span style="font-size:16px;line-height:150%;">&gt;0.05</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量均低于杨江涛等</span><sup><span style="font-size:16px;line-height:150%;">[1]</span></sup><span style="font-size:16px;line-height:150%;font-family:宋体;">的报道（总皂苷含量</span><span style="font-size:16px;line-height:150%;">≥5%</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:12.0pt;line-height:150%;">5.</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要结论</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">本实验采用分光光度计测定苜草素中总皂苷的含量，标准曲线的线性关系良好（</span><i><span style="font-size:12.0pt;line-height:150%;">R</span></i><sup><span style="font-size:12.0pt;line-height:150%;">2</span></sup><span style="font-size:12.0pt;line-height:150%;">=1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">），检测</span><span style="font-size:12.0pt;line-height:150%;">4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量为</span><span style="font-size:12.0pt;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">。</span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"><br/></span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">主要参考文献：</span><span style="font-size:10.5pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;">[1]</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">杨江涛</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">董晓芳</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">佟建明</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">刘晴雪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">张琪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">吴莹莹</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">苜草素多糖、黄酮和皂苷对小鼠巨噬细胞</span><span style="font-size:10.5pt;line-height:150%;">RAW264.7B-</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">防御素基因表达的影响</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">畜牧兽医学报</span><span style="font-size:10.5pt;line-height:150%;">,2010,41(5):608–614.</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测操作人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">焦晓林</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测负责人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">高微微</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">报告日期：</span><span style="font-size:12.0pt;line-height:150%;">&nbsp;<u>2014</u></span><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">年</span></u><u><span style="font-size:12.0pt;line-height:150%;">7</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">月</span></u><u><span style="font-size:12.0pt;line-height:150%;">30</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">日</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:left;"><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><span><span style="line-height:24px;"><br/></span></span></p>', '10Kg/袋', 0, 0, 0, 0, 0, 0);
INSERT INTO `l_product` VALUES (2, '苜草素2号 10Kg装 粉状	原粉', '蓝海洋科技', 0.00, '/public/uploads/product/2.jpg', '/public/uploads/product/thumb/2.jpg', '苜草素 10kg 检测 标准 检测报告', '<p><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;"><br/></span></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;"><img src="http://www.mucaosu.com/public/uploads/product/2_1.jpg"alt=""width="800"height="1206"title=""align=""/><br/></span></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;"><img src="http://www.mucaosu.com/public/uploads/product/2_2.jpg"alt=""width="800"height="1191"title=""align=""/><br/></span></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;">苜草素2号产品说明书<span></span></span></p><hr/><p class="MsoNormal"style="text-indent:21.1pt;"><b><span style="line-height:150%;font-family:黑体;">&nbsp;</span></b><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">是一种以紫花苜蓿为主要原料的天然植物提取物，具有增强动物免疫机能、改善畜产品质量、促进畜禽生长的作用，综合效果可替代饲用抗生素，而且无毒副作用，是一种新型的绿色的有效保护畜禽健康的饲料添加剂。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要成份</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">的有效活性成分包括：</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">多糖≥<span>15%</span>、</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">黄酮</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">≥<span>5%</span>、皂甙≥<span>5%</span>。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">产品性状<span>&nbsp;</span></span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">棕黄色粉末，气味芳香。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要功能<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、提高母源抗体水平，增强幼龄动物的抵抗力。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;2</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、增强动物机体免疫力，促进生长。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;3</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、调节脂肪代谢，有效预防脂肪肝等营养代谢病。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、改善肉质和风味。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;5</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、替代饲用抗生素。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;<span style="color:blue;">&nbsp;6</span></span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:blue;">、通过苜蓿黄酮中类雌激素的双向调节作用，提高生猪产子数和蛋鸡产蛋率。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">作用机理<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">苜草素中的多糖、黄酮和皂苷等有效成分可以促进动物免疫器官的发育，增强<span>T</span>、<span>B</span>淋巴细胞的活化，提高血清抗体水平，从而显著增强机体的免疫功能，提高母源抗体水平。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">另外，苜草素可以降低脂肪合成酶和脂蛋白酯酶的活性，反而提高脂蛋白酯酶和解偶联蛋白的活性，从而改善了机体的脂肪代谢过程，降低了脂肪沉积，促进了肌肉发育，改善了畜产品质量和风味。<span>`</span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;">&nbsp;&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;font-family:宋体;">应用效果</span></b><span style="font-size:12.0pt;"></span></p><p class="MsoNormal"align="left"style="text-indent:24pt;"><span style="font-size:12pt;line-height:150%;font-family:宋体;">1</span><span style="font-size:12pt;line-height:150%;font-family:宋体;">、调节畜禽机体免疫功能，提高畜禽自身的抗病力，增强抗逆性，预防幼畜禽腹泻，综合效果可替代饲用抗生素。<span></span></span></p><p class="MsoNormal"align="left"style="text-indent:24pt;"><span style="font-size:12pt;line-height:150%;font-family:宋体;">2</span><span style="font-size:12pt;line-height:150%;font-family:宋体;">、苜草素可分别提高猪和肉鸡体增重<span>15%</span>和<span>7%</span>，提高猪和肉鸡的饲料转化率<span>15%</span>和<span>3%</span>；提高蛋鸡产蛋率<span>4%</span>，降低死淘率<span>22%</span>；提高母猪产仔率<span>10%</span>，提高仔猪成活率<span>6%</span>。<span></span></span></p><p class="MsoNormal"align="left"style="text-indent:24pt;"><span style="font-size:12pt;line-height:150%;font-family:宋体;">3</span><span style="font-size:12pt;line-height:150%;font-family:宋体;">、调节脂肪代谢、减少腹脂沉积，提高肉鸡胸肌率<span>7%</span>，降低腹脂率<span>15%</span>，降低鸡蛋胆固醇含量，改善肉质和风味。<span></span></span></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;">使用方法</span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:red;">1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:red;">、苜草素产品分为原粉和预混剂两种。原粉吸潮性强，在使用前应先作预混处理，以避免吸潮结块。预混剂可直接使用，但应注意苜草素的含量。<span></span></span></p><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:red;">2</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:red;">、苜草素产品应在通风阴凉处保存，开封后注意防潮。<span></span></span></p><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:red;">3</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:red;">、在配合饲料中的推荐用量及使用方法。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">猪配合饲料</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p><table class="MsoNormalTable"border="1"cellspacing="0"cellpadding="0"style="border-collapse:collapse;border:none;"><tbody><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">使用对象<span></span></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">添加量（公斤<span>/</span>吨）<span></span></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">乳猪（断奶前）</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.15~0.2</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">仔猪（断奶</span><span>-20</span><span style="font-family:宋体;">公斤）</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.6~0.7</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">育肥猪（</span><span>20</span><span style="font-family:宋体;">公斤</span><span>-</span><span style="font-family:宋体;">屠宰）</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.3~0.5</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">母猪</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.4~0.5</span></p></td></tr></tbody></table><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">鸡配合饲料</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p><table class="MsoNormalTable"border="1"cellspacing="0"cellpadding="0"style="border-collapse:collapse;border:none;"><tbody><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">使用对象<span></span></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">添加量（公斤<span>/</span>吨）<span></span></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">雏鸡（</span><span>0-21</span><span style="font-family:宋体;">天，包括肉鸡和蛋鸡）</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.15~0.2</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">肉鸡（</span><span>21-49</span><span style="font-family:宋体;">天）</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.5~0.6</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">育成鸡（</span><span>21-110</span><span style="font-family:宋体;">天）</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.1~0.15</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">产蛋鸡（</span><span>110-500</span><span style="font-family:宋体;">天）</span><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></b></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.15~0.2</span></p></td></tr><tr><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span style="font-family:宋体;">种鸡（收集种蛋期间）</span></p></td><td width="284"valign="top"style="border:solid windowtext 1.0pt;"><p class="MsoNormal"><span>0.4~0.5</span></p></td></tr></tbody></table><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:red;"></span></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;">保质期为<span>2</span>年。</span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p><br/></p><p><br/></p><p><span style="color:#006600;font-size:24px;">中国农业科学畜牧研究所检测报告</span></p><hr/><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;无公害饲料添加剂－"苜草素"是由中国农科院畜牧研究所研制，于2003年12月被批准为饲料添加剂新产品，新产品证书号为新饲证字(2003)06号。"苜草素"是一个具有我国自主知识产权的无公害天然饲料添加剂新产品，目前已经获得国家发明专利。</span></p><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过实践证明，在肉仔鸡饲养过程中添加"苜草素"，可以提高肉仔鸡平均日增重11.92%，提高饲料转化效率2.65%，降低腹脂14.7%，提高胸肌率8.5%，能够减少早期肉仔鸡腹泻，提高存活率9.3%；可以抑制肉仔鸡不同生长阶段肠道内大肠杆菌的繁殖，降低菌群数量。在同等饲养条件下，添加"苜草素"仔猪平均日增重提高5.5%，饲料转化效率提高1.3%，有效的提高了猪肉产品的质量，保持了猪肉产品天然的风味和口感。</span></p><p><span style="font-size:16px;">检测报告：</span></p><p><span style="font-size:16px;">中国医学科学院药用植物研究所</span><span style="line-height:1.5;font-size:16px;">检测报告</span></p><p><span style="line-height:1.5;font-size:16px;"><b><span style="font-size:16px;">1.</span></b><b><span style="font-size:16px;">样品基本信息</span></b><br/></span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检单位：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">中国农业科学院畜牧研究所</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测样品：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">样品数量：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;4</span></u><u><span style="font-size:14px;">份</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检日期：</span><u><span style="font-size:14px;">&nbsp;2014</span></u><u><span style="font-size:14px;">年</span></u><u><span style="font-size:14px;">7</span></u><u><span style="font-size:14px;">月</span></u><u><span style="font-size:14px;">15</span></u><u><span style="font-size:14px;">日</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测内容：</span><u><span style="font-size:14px;">&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素样品中总皂苷的含量检测</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">编号方法：</span><u><span style="font-size:14px;">&nbsp;4</span></u><u><span style="font-size:14px;">份送检样品分别编号为</span></u><u><span style="font-size:14px;">1-4</span></u><u><span style="font-size:14px;">，见图</span></u><u><span style="font-size:14px;">1&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><u><span style="font-size:14px;"><br/></span></u></p><p><!--[if gte vml 1]><![endif]--><!--[if!vml]--><img width="467"height="192"src="/Public/Uploads/image/201507/8typhzh.jpg"title="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"align=""alt="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"/></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">2.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测目标</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">利用分光光度法测定送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">3.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测方法</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:16px;line-height:150%;">3.1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">检测波长的确定</span></p><p class="MsoNormal"style="margin-left:18.0pt;text-align:justify;text-indent:-18.0pt;"><b><span style="font-size:12.0pt;line-height:150%;"><span style="font-size:16px;">4.</span><span style="font-weight:normal;font-size:16px;line-height:normal;font-family:''Times New Roman'';">&nbsp;</span></span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测结果</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量在</span><span style="font-size:16px;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:16px;line-height:150%;font-family:宋体;">之间，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品含量最高，</span><span style="font-size:16px;line-height:150%;">1-3</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品的总皂苷含量接近，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份样品间差异均不显著（表</span><span style="font-size:16px;line-height:150%;">1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">，</span><i><span style="font-size:16px;line-height:150%;">P</span></i><span style="font-size:16px;line-height:150%;">&gt;0.05</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量均低于杨江涛等</span><sup><span style="font-size:16px;line-height:150%;">[1]</span></sup><span style="font-size:16px;line-height:150%;font-family:宋体;">的报道（总皂苷含量</span><span style="font-size:16px;line-height:150%;">≥5%</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:12.0pt;line-height:150%;">5.</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要结论</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">本实验采用分光光度计测定苜草素中总皂苷的含量，标准曲线的线性关系良好（</span><i><span style="font-size:12.0pt;line-height:150%;">R</span></i><sup><span style="font-size:12.0pt;line-height:150%;">2</span></sup><span style="font-size:12.0pt;line-height:150%;">=1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">），检测</span><span style="font-size:12.0pt;line-height:150%;">4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量为</span><span style="font-size:12.0pt;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">。</span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"><br/></span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">主要参考文献：</span><span style="font-size:10.5pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;">[1]</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">杨江涛</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">董晓芳</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">佟建明</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">刘晴雪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">张琪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">吴莹莹</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">苜草素多糖、黄酮和皂苷对小鼠巨噬细胞</span><span style="font-size:10.5pt;line-height:150%;">RAW264.7B-</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">防御素基因表达的影响</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">畜牧兽医学报</span><span style="font-size:10.5pt;line-height:150%;">,2010,41(5):608–614.</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测操作人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">焦晓林</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测负责人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">高微微</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">报告日期：</span><span style="font-size:12.0pt;line-height:150%;">&nbsp;<u>2014</u></span><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">年</span></u><u><span style="font-size:12.0pt;line-height:150%;">7</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">月</span></u><u><span style="font-size:12.0pt;line-height:150%;">30</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">日</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:left;"><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><span><span style="line-height:24px;"><br/></span></span></p>', '10KG/袋', 0, 0, 0, 0, 0, 0);
INSERT INTO `l_product` VALUES (3, '苜草素3号 妊娠母猪专用 粉状 配料  1000g装', '蓝海洋科技', 0.00, '/public/uploads/product/3.jpg', '/public/uploads/product/thumb/3.jpg', '苜草素饲料添加剂 妊娠母猪 妊娠母猪专用 饲料添加剂 母猪饲料添加剂 10kg 检测 标准 检测报告', '<p><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><img src="http://www.mucaosu.com/public/uploads/product/3_1.jpg"alt=""width="800"height="1206"title=""align=""/></p><p class="MsoNormal"align="center"style="text-align:center;"><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;"><img src="http://www.mucaosu.com/public/uploads/product/3_2.jpg"alt=""width="800"height="1191"title=""align=""/><br/></span></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;">苜草素3号产品说明书<span></span></span></p><hr/><p class="MsoNormal"style="text-indent:21.1pt;"><b><span style="line-height:150%;font-family:黑体;">&nbsp;</span></b><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">是一种以紫花苜蓿为主要原料的天然植物提取物，具有增强动物免疫机能、改善畜产品质量、促进畜禽生长的作用，综合效果可替代饲用抗生素，而且无毒副作用，是一种新型的绿色的有效保护畜禽健康的饲料添加剂。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要成份</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">的有效活性成分包括：</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">多糖≥<span>15%</span>、</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">黄酮</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">≥<span>5%</span>、皂甙≥<span>5%</span>。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">产品性状<span>&nbsp;</span></span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">棕黄色粉末，气味芳香。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要功能<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、提高母源抗体水平，增强幼龄动物的抵抗力。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;2</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、增强动物机体免疫力，促进生长。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;3</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、调节脂肪代谢，有效预防脂肪肝等营养代谢病。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、改善肉质和风味。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;5</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、替代饲用抗生素。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;<span style="color:blue;">&nbsp;6</span></span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:blue;">、通过苜蓿黄酮中类雌激素的双向调节作用，提高生猪产子数和蛋鸡产蛋率。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">作用机理<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">苜草素中的多糖、黄酮和皂苷等有效成分可以促进动物免疫器官的发育，增强<span>T</span>、<span>B</span>淋巴细胞的活化，提高血清抗体水平，从而显著增强机体的免疫功能，提高母源抗体水平。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">另外，苜草素可以降低脂肪合成酶和脂蛋白酯酶的活性，反而提高脂蛋白酯酶和解偶联蛋白的活性，从而改善了机体的脂肪代谢过程，降低了脂肪沉积，促进了肌肉发育，改善了畜产品质量和风味。<span>`</span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;">&nbsp;&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;font-family:宋体;">应用效果</span></b><span style="font-size:12.0pt;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;"></span></b></p><p class="MsoNormal"><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;1</span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">、调节妊娠母猪机体免疫功能，提高妊娠母猪自身的抗病力，增强抗逆性，<span></span></span></b></b></p><p class="MsoNormal"><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">预防产后仔猪腹泻，综合效果可替代饲用抗生素。<span></span></span></b></b></p><p class="MsoNormal"><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;2</span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">、苜草素可提高产仔率<span>10%</span>，提高仔猪成活率<span>6%</span>，提高饲料转化率<span>15%</span>，降低<span></span></span></b></b></p><p class="MsoNormal"><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">死淘率<span>20%</span>。<span></span></span></b></b></p><p><br/></p><br/><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;">使用方法</span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span><span style="line-height:24px;">每吨全价料添加1000g进行配料。</span></span></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/>保质期为<span>2</span>年。</span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p><br/></p><p><br/></p><p><span style="color:#006600;font-size:24px;">中国农业科学畜牧研究所检测报告</span></p><hr/><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;无公害饲料添加剂－"苜草素"是由中国农科院畜牧研究所研制，于2003年12月被批准为饲料添加剂新产品，新产品证书号为新饲证字(2003)06号。"苜草素"是一个具有我国自主知识产权的无公害天然饲料添加剂新产品，目前已经获得国家发明专利。</span></p><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过实践证明，在肉仔鸡饲养过程中添加"苜草素"，可以提高肉仔鸡平均日增重11.92%，提高饲料转化效率2.65%，降低腹脂14.7%，提高胸肌率8.5%，能够减少早期肉仔鸡腹泻，提高存活率9.3%；可以抑制肉仔鸡不同生长阶段肠道内大肠杆菌的繁殖，降低菌群数量。在同等饲养条件下，添加"苜草素"仔猪平均日增重提高5.5%，饲料转化效率提高1.3%，有效的提高了猪肉产品的质量，保持了猪肉产品天然的风味和口感。</span></p><p><span style="font-size:16px;">检测报告：</span></p><p><span style="font-size:16px;">中国医学科学院药用植物研究所</span><span style="line-height:1.5;font-size:16px;">检测报告</span></p><p><span style="line-height:1.5;font-size:16px;"><b><span style="font-size:16px;">1.</span></b><b><span style="font-size:16px;">样品基本信息</span></b><br/></span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检单位：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">中国农业科学院畜牧研究所</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测样品：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">样品数量：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;4</span></u><u><span style="font-size:14px;">份</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检日期：</span><u><span style="font-size:14px;">&nbsp;2014</span></u><u><span style="font-size:14px;">年</span></u><u><span style="font-size:14px;">7</span></u><u><span style="font-size:14px;">月</span></u><u><span style="font-size:14px;">15</span></u><u><span style="font-size:14px;">日</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测内容：</span><u><span style="font-size:14px;">&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素样品中总皂苷的含量检测</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">编号方法：</span><u><span style="font-size:14px;">&nbsp;4</span></u><u><span style="font-size:14px;">份送检样品分别编号为</span></u><u><span style="font-size:14px;">1-4</span></u><u><span style="font-size:14px;">，见图</span></u><u><span style="font-size:14px;">1&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><u><span style="font-size:14px;"><br/></span></u></p><p><!--[if gte vml 1]><![endif]--><!--[if!vml]--><img width="467"height="192"src="/Public/Uploads/image/201507/8typhzh.jpg"title="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"align=""alt="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"/></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">2.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测目标</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">利用分光光度法测定送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">3.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测方法</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:16px;line-height:150%;">3.1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">检测波长的确定</span></p><p class="MsoNormal"style="margin-left:18.0pt;text-align:justify;text-indent:-18.0pt;"><b><span style="font-size:12.0pt;line-height:150%;"><span style="font-size:16px;">4.</span><span style="font-weight:normal;font-size:16px;line-height:normal;font-family:''Times New Roman'';">&nbsp;</span></span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测结果</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量在</span><span style="font-size:16px;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:16px;line-height:150%;font-family:宋体;">之间，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品含量最高，</span><span style="font-size:16px;line-height:150%;">1-3</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品的总皂苷含量接近，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份样品间差异均不显著（表</span><span style="font-size:16px;line-height:150%;">1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">，</span><i><span style="font-size:16px;line-height:150%;">P</span></i><span style="font-size:16px;line-height:150%;">&gt;0.05</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量均低于杨江涛等</span><sup><span style="font-size:16px;line-height:150%;">[1]</span></sup><span style="font-size:16px;line-height:150%;font-family:宋体;">的报道（总皂苷含量</span><span style="font-size:16px;line-height:150%;">≥5%</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:12.0pt;line-height:150%;">5.</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要结论</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">本实验采用分光光度计测定苜草素中总皂苷的含量，标准曲线的线性关系良好（</span><i><span style="font-size:12.0pt;line-height:150%;">R</span></i><sup><span style="font-size:12.0pt;line-height:150%;">2</span></sup><span style="font-size:12.0pt;line-height:150%;">=1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">），检测</span><span style="font-size:12.0pt;line-height:150%;">4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量为</span><span style="font-size:12.0pt;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">。</span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"><br/></span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">主要参考文献：</span><span style="font-size:10.5pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;">[1]</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">杨江涛</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">董晓芳</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">佟建明</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">刘晴雪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">张琪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">吴莹莹</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">苜草素多糖、黄酮和皂苷对小鼠巨噬细胞</span><span style="font-size:10.5pt;line-height:150%;">RAW264.7B-</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">防御素基因表达的影响</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">畜牧兽医学报</span><span style="font-size:10.5pt;line-height:150%;">,2010,41(5):608–614.</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测操作人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">焦晓林</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测负责人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">高微微</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">报告日期：</span><span style="font-size:12.0pt;line-height:150%;">&nbsp;<u>2014</u></span><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">年</span></u><u><span style="font-size:12.0pt;line-height:150%;">7</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">月</span></u><u><span style="font-size:12.0pt;line-height:150%;">30</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">日</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:left;"><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><span><span style="line-height:24px;"><br/></span></span></p>', '1000克/袋', 0, 0, 0, 0, 0, 0);
INSERT INTO `l_product` VALUES (4, '苜草素4号 哺乳母猪专用  粉状 配料  1000g装', '蓝海洋科技', 0.00, '/public/uploads/product/4.jpg', '/public/uploads/product/thumb/4.jpg', '哺乳母猪 哺乳母猪专用 苜草素饲料添加剂 饲料添加剂 母猪饲料添加剂  检测 标准 检测报告', '<p><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><img src="http://www.mucaosu.com/public/uploads/product/4_1.jpg"alt=""width="800"height="1183"title=""align=""/></p><p class="MsoNormal"align="center"style="text-align:center;"><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;"><img src="http://www.mucaosu.com/public/uploads/product/4_2.jpg"alt=""width="800"height="1232"title=""align=""/><br/></span></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;">苜草素4号产品说明书<span></span></span></p><hr/><p class="MsoNormal"style="text-indent:21.1pt;"><b><span style="line-height:150%;font-family:黑体;">&nbsp;</span></b><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">是一种以紫花苜蓿为主要原料的天然植物提取物，具有增强动物免疫机能、改善畜产品质量、促进畜禽生长的作用，综合效果可替代饲用抗生素，而且无毒副作用，是一种新型的绿色的有效保护畜禽健康的饲料添加剂。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要成份</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">的有效活性成分包括：</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">多糖≥<span>15%</span>、</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">黄酮</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">≥<span>5%</span>、皂甙≥<span>5%</span>。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">产品性状<span>&nbsp;</span></span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">棕黄色粉末，气味芳香。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要功能<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、提高母源抗体水平，增强幼龄动物的抵抗力。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;2</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、增强动物机体免疫力，促进生长。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;3</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、调节脂肪代谢，有效预防脂肪肝等营养代谢病。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、改善肉质和风味。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;5</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、替代饲用抗生素。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;<span style="color:blue;">&nbsp;6</span></span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:blue;">、通过苜蓿黄酮中类雌激素的双向调节作用，提高生猪产子数和蛋鸡产蛋率。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">作用机理<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">苜草素中的多糖、黄酮和皂苷等有效成分可以促进动物免疫器官的发育，增强<span>T</span>、<span>B</span>淋巴细胞的活化，提高血清抗体水平，从而显著增强机体的免疫功能，提高母源抗体水平。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">另外，苜草素可以降低脂肪合成酶和脂蛋白酯酶的活性，反而提高脂蛋白酯酶和解偶联蛋白的活性，从而改善了机体的脂肪代谢过程，降低了脂肪沉积，促进了肌肉发育，改善了畜产品质量和风味。<span>`</span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;">&nbsp;&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;font-family:宋体;">应用效果</span></b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;</span></b></b></p><p class="MsoNormal"><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;1</span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">、增强产后母猪免疫机能，提高产后母猪的抗病力，增强抗逆性，保证母乳<span></span></span></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">充足，预防产后仔猪腹泻，综合效果可替代抗生素。<span></span></span></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;2</span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">、可有效提高仔猪成活率<span>6%</span>，提高母猪饲料转化率<span>15%</span>，提高配种率<span>17%</span>。<span></span></span></b></p></span></b></b></p><p class="MsoNormal"><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><span></span><span></span></span></b></b></p><p><br/></p><br/><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;">使用方法</span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span><span style="line-height:24px;">每吨全价料添加1000g进行配料。</span></span></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/>保质期为<span>2</span>年。</span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p><br/></p><p><br/></p><p><span style="color:#006600;font-size:24px;">中国农业科学畜牧研究所检测报告</span></p><hr/><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;无公害饲料添加剂－"苜草素"是由中国农科院畜牧研究所研制，于2003年12月被批准为饲料添加剂新产品，新产品证书号为新饲证字(2003)06号。"苜草素"是一个具有我国自主知识产权的无公害天然饲料添加剂新产品，目前已经获得国家发明专利。</span></p><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过实践证明，在肉仔鸡饲养过程中添加"苜草素"，可以提高肉仔鸡平均日增重11.92%，提高饲料转化效率2.65%，降低腹脂14.7%，提高胸肌率8.5%，能够减少早期肉仔鸡腹泻，提高存活率9.3%；可以抑制肉仔鸡不同生长阶段肠道内大肠杆菌的繁殖，降低菌群数量。在同等饲养条件下，添加"苜草素"仔猪平均日增重提高5.5%，饲料转化效率提高1.3%，有效的提高了猪肉产品的质量，保持了猪肉产品天然的风味和口感。</span></p><p><span style="font-size:16px;">检测报告：</span></p><p><span style="font-size:16px;">中国医学科学院药用植物研究所</span><span style="line-height:1.5;font-size:16px;">检测报告</span></p><p><span style="line-height:1.5;font-size:16px;"><b><span style="font-size:16px;">1.</span></b><b><span style="font-size:16px;">样品基本信息</span></b><br/></span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检单位：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">中国农业科学院畜牧研究所</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测样品：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">样品数量：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;4</span></u><u><span style="font-size:14px;">份</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检日期：</span><u><span style="font-size:14px;">&nbsp;2014</span></u><u><span style="font-size:14px;">年</span></u><u><span style="font-size:14px;">7</span></u><u><span style="font-size:14px;">月</span></u><u><span style="font-size:14px;">15</span></u><u><span style="font-size:14px;">日</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测内容：</span><u><span style="font-size:14px;">&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素样品中总皂苷的含量检测</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">编号方法：</span><u><span style="font-size:14px;">&nbsp;4</span></u><u><span style="font-size:14px;">份送检样品分别编号为</span></u><u><span style="font-size:14px;">1-4</span></u><u><span style="font-size:14px;">，见图</span></u><u><span style="font-size:14px;">1&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><u><span style="font-size:14px;"><br/></span></u></p><p><!--[if gte vml 1]><![endif]--><!--[if!vml]--><img width="467"height="192"src="/Public/Uploads/image/201507/8typhzh.jpg"title="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"align=""alt="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"/></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">2.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测目标</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">利用分光光度法测定送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">3.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测方法</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:16px;line-height:150%;">3.1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">检测波长的确定</span></p><p class="MsoNormal"style="margin-left:18.0pt;text-align:justify;text-indent:-18.0pt;"><b><span style="font-size:12.0pt;line-height:150%;"><span style="font-size:16px;">4.</span><span style="font-weight:normal;font-size:16px;line-height:normal;font-family:''Times New Roman'';">&nbsp;</span></span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测结果</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量在</span><span style="font-size:16px;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:16px;line-height:150%;font-family:宋体;">之间，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品含量最高，</span><span style="font-size:16px;line-height:150%;">1-3</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品的总皂苷含量接近，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份样品间差异均不显著（表</span><span style="font-size:16px;line-height:150%;">1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">，</span><i><span style="font-size:16px;line-height:150%;">P</span></i><span style="font-size:16px;line-height:150%;">&gt;0.05</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量均低于杨江涛等</span><sup><span style="font-size:16px;line-height:150%;">[1]</span></sup><span style="font-size:16px;line-height:150%;font-family:宋体;">的报道（总皂苷含量</span><span style="font-size:16px;line-height:150%;">≥5%</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:12.0pt;line-height:150%;">5.</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要结论</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">本实验采用分光光度计测定苜草素中总皂苷的含量，标准曲线的线性关系良好（</span><i><span style="font-size:12.0pt;line-height:150%;">R</span></i><sup><span style="font-size:12.0pt;line-height:150%;">2</span></sup><span style="font-size:12.0pt;line-height:150%;">=1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">），检测</span><span style="font-size:12.0pt;line-height:150%;">4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量为</span><span style="font-size:12.0pt;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">。</span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"><br/></span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">主要参考文献：</span><span style="font-size:10.5pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;">[1]</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">杨江涛</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">董晓芳</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">佟建明</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">刘晴雪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">张琪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">吴莹莹</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">苜草素多糖、黄酮和皂苷对小鼠巨噬细胞</span><span style="font-size:10.5pt;line-height:150%;">RAW264.7B-</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">防御素基因表达的影响</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">畜牧兽医学报</span><span style="font-size:10.5pt;line-height:150%;">,2010,41(5):608–614.</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测操作人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">焦晓林</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测负责人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">高微微</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">报告日期：</span><span style="font-size:12.0pt;line-height:150%;">&nbsp;<u>2014</u></span><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">年</span></u><u><span style="font-size:12.0pt;line-height:150%;">7</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">月</span></u><u><span style="font-size:12.0pt;line-height:150%;">30</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">日</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:left;"><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><span><span style="line-height:24px;"><br/></span></span></p>', '1000克/袋', 0, 0, 0, 0, 0, 0);
INSERT INTO `l_product` VALUES (5, '苜草素5号 产床仔猪专用 颗粒 直喂  1000g装', '蓝海洋科技', 0.00, '/public/uploads/product/5.jpg', '/public/uploads/product/thumb/5.jpg', '产床仔猪 产床仔猪专用 苜草素饲料添加剂 饲料添加剂  检测 标准 检测报告', '<p><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><img src="http://www.mucaosu.com/public/uploads/product/5_1.jpg"alt=""width="800"height="1183"title=""align=""/></p><p class="MsoNormal"align="center"style="text-align:center;"><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;"><img src="http://www.mucaosu.com/public/uploads/product/5_2.jpg"alt=""width="800"height="1232"title=""align=""/><br/></span></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;">苜草素5号产品说明书<span></span></span></p><hr/><p class="MsoNormal"style="text-indent:21.1pt;"><b><span style="line-height:150%;font-family:黑体;">&nbsp;</span></b><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">是一种以紫花苜蓿为主要原料的天然植物提取物，具有增强动物免疫机能、改善畜产品质量、促进畜禽生长的作用，综合效果可替代饲用抗生素，而且无毒副作用，是一种新型的绿色的有效保护畜禽健康的饲料添加剂。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要成份</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">的有效活性成分包括：</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">多糖≥<span>15%</span>、</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">黄酮</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">≥<span>5%</span>、皂甙≥<span>5%</span>。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">产品性状<span>&nbsp;</span></span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">棕黄色粉末，气味芳香。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要功能<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、提高母源抗体水平，增强幼龄动物的抵抗力。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;2</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、增强动物机体免疫力，促进生长。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;3</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、调节脂肪代谢，有效预防脂肪肝等营养代谢病。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、改善肉质和风味。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;5</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、替代饲用抗生素。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;<span style="color:blue;">&nbsp;6</span></span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:blue;">、通过苜蓿黄酮中类雌激素的双向调节作用，提高生猪产子数和蛋鸡产蛋率。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">作用机理<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">苜草素中的多糖、黄酮和皂苷等有效成分可以促进动物免疫器官的发育，增强<span>T</span>、<span>B</span>淋巴细胞的活化，提高血清抗体水平，从而显著增强机体的免疫功能，提高母源抗体水平。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">另外，苜草素可以降低脂肪合成酶和脂蛋白酯酶的活性，反而提高脂蛋白酯酶和解偶联蛋白的活性，从而改善了机体的脂肪代谢过程，降低了脂肪沉积，促进了肌肉发育，改善了畜产品质量和风味。<span>`</span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;">&nbsp;&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;font-family:宋体;">应用效果</span></b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;</span></b></b><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;</span></b></b></b></p><p class="MsoNormal"><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;&nbsp;预防幼仔猪腹泻，调节机体免疫功能，提高幼仔猪的抗病力，促进后期生产<span></span></span></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">提高产床幼仔猪成活率<span>6%</span>以上。综合效果可替代抗生素。<span></span></span></b></p></span></b></b></b></p><p class="MsoNormal"><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"></span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><span></span><span></span><span></span><span></span></span></b></b></b></p><p><br/></p><p class="MsoNormal"><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><span></span><span></span></span></b></b></p><p><br/></p><br/><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;">使用方法</span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span><span style="line-height:24px;">每吨全价料添加1000g进行饲喂。</span></span></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/>保质期为<span>2</span>年。</span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p><br/></p><p><br/></p><p><span style="color:#006600;font-size:24px;">中国农业科学畜牧研究所检测报告</span></p><hr/><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;无公害饲料添加剂－"苜草素"是由中国农科院畜牧研究所研制，于2003年12月被批准为饲料添加剂新产品，新产品证书号为新饲证字(2003)06号。"苜草素"是一个具有我国自主知识产权的无公害天然饲料添加剂新产品，目前已经获得国家发明专利。</span></p><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过实践证明，在肉仔鸡饲养过程中添加"苜草素"，可以提高肉仔鸡平均日增重11.92%，提高饲料转化效率2.65%，降低腹脂14.7%，提高胸肌率8.5%，能够减少早期肉仔鸡腹泻，提高存活率9.3%；可以抑制肉仔鸡不同生长阶段肠道内大肠杆菌的繁殖，降低菌群数量。在同等饲养条件下，添加"苜草素"仔猪平均日增重提高5.5%，饲料转化效率提高1.3%，有效的提高了猪肉产品的质量，保持了猪肉产品天然的风味和口感。</span></p><p><span style="font-size:16px;">检测报告：</span></p><p><span style="font-size:16px;">中国医学科学院药用植物研究所</span><span style="line-height:1.5;font-size:16px;">检测报告</span></p><p><span style="line-height:1.5;font-size:16px;"><b><span style="font-size:16px;">1.</span></b><b><span style="font-size:16px;">样品基本信息</span></b><br/></span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检单位：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">中国农业科学院畜牧研究所</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测样品：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">样品数量：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;4</span></u><u><span style="font-size:14px;">份</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检日期：</span><u><span style="font-size:14px;">&nbsp;2014</span></u><u><span style="font-size:14px;">年</span></u><u><span style="font-size:14px;">7</span></u><u><span style="font-size:14px;">月</span></u><u><span style="font-size:14px;">15</span></u><u><span style="font-size:14px;">日</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测内容：</span><u><span style="font-size:14px;">&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素样品中总皂苷的含量检测</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">编号方法：</span><u><span style="font-size:14px;">&nbsp;4</span></u><u><span style="font-size:14px;">份送检样品分别编号为</span></u><u><span style="font-size:14px;">1-4</span></u><u><span style="font-size:14px;">，见图</span></u><u><span style="font-size:14px;">1&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><u><span style="font-size:14px;"><br/></span></u></p><p><!--[if gte vml 1]><![endif]--><!--[if!vml]--><img width="467"height="192"src="/Public/Uploads/image/201507/8typhzh.jpg"title="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"align=""alt="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"/></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">2.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测目标</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">利用分光光度法测定送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">3.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测方法</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:16px;line-height:150%;">3.1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">检测波长的确定</span></p><p class="MsoNormal"style="margin-left:18.0pt;text-align:justify;text-indent:-18.0pt;"><b><span style="font-size:12.0pt;line-height:150%;"><span style="font-size:16px;">4.</span><span style="font-weight:normal;font-size:16px;line-height:normal;font-family:''Times New Roman'';">&nbsp;</span></span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测结果</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量在</span><span style="font-size:16px;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:16px;line-height:150%;font-family:宋体;">之间，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品含量最高，</span><span style="font-size:16px;line-height:150%;">1-3</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品的总皂苷含量接近，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份样品间差异均不显著（表</span><span style="font-size:16px;line-height:150%;">1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">，</span><i><span style="font-size:16px;line-height:150%;">P</span></i><span style="font-size:16px;line-height:150%;">&gt;0.05</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量均低于杨江涛等</span><sup><span style="font-size:16px;line-height:150%;">[1]</span></sup><span style="font-size:16px;line-height:150%;font-family:宋体;">的报道（总皂苷含量</span><span style="font-size:16px;line-height:150%;">≥5%</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:12.0pt;line-height:150%;">5.</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要结论</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">本实验采用分光光度计测定苜草素中总皂苷的含量，标准曲线的线性关系良好（</span><i><span style="font-size:12.0pt;line-height:150%;">R</span></i><sup><span style="font-size:12.0pt;line-height:150%;">2</span></sup><span style="font-size:12.0pt;line-height:150%;">=1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">），检测</span><span style="font-size:12.0pt;line-height:150%;">4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量为</span><span style="font-size:12.0pt;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">。</span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"><br/></span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">主要参考文献：</span><span style="font-size:10.5pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;">[1]</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">杨江涛</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">董晓芳</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">佟建明</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">刘晴雪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">张琪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">吴莹莹</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">苜草素多糖、黄酮和皂苷对小鼠巨噬细胞</span><span style="font-size:10.5pt;line-height:150%;">RAW264.7B-</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">防御素基因表达的影响</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">畜牧兽医学报</span><span style="font-size:10.5pt;line-height:150%;">,2010,41(5):608–614.</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测操作人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">焦晓林</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测负责人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">高微微</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">报告日期：</span><span style="font-size:12.0pt;line-height:150%;">&nbsp;<u>2014</u></span><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">年</span></u><u><span style="font-size:12.0pt;line-height:150%;">7</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">月</span></u><u><span style="font-size:12.0pt;line-height:150%;">30</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">日</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:left;"><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><span><span style="line-height:24px;"><br/></span></span></p>', '1000克/袋', 0, 0, 0, 0, 0, 0);
INSERT INTO `l_product` VALUES (6, '苜草素6号 保育仔猪专用  粉状 配料  1000g装', '蓝海洋科技', 0.00, '/public/uploads/product/6.jpg', '/public/uploads/product/thumb/6.jpg', '保育仔猪 保育仔猪专用 苜草素饲料添加剂 饲料添加剂  检测 标准 检测报告', '<p><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><img src="http://www.mucaosu.com/public/uploads/product/6_1.jpg"alt=""width="800"height="1183"title=""align=""/></p><p class="MsoNormal"align="center"style="text-align:center;"><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;"><img src="http://www.mucaosu.com/public/uploads/product/6_2.jpg"alt=""width="800"height="1232"title=""align=""/><br/></span></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;">苜草素6号产品说明书<span></span></span></p><hr/><p class="MsoNormal"style="text-indent:21.1pt;"><b><span style="line-height:150%;font-family:黑体;">&nbsp;</span></b><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">是一种以紫花苜蓿为主要原料的天然植物提取物，具有增强动物免疫机能、改善畜产品质量、促进畜禽生长的作用，综合效果可替代饲用抗生素，而且无毒副作用，是一种新型的绿色的有效保护畜禽健康的饲料添加剂。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要成份</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">的有效活性成分包括：</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">多糖≥<span>15%</span>、</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">黄酮</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">≥<span>5%</span>、皂甙≥<span>5%</span>。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">产品性状<span>&nbsp;</span></span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">棕黄色粉末，气味芳香。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要功能<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、提高母源抗体水平，增强幼龄动物的抵抗力。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;2</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、增强动物机体免疫力，促进生长。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;3</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、调节脂肪代谢，有效预防脂肪肝等营养代谢病。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、改善肉质和风味。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;5</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、替代饲用抗生素。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;<span style="color:blue;">&nbsp;6</span></span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:blue;">、通过苜蓿黄酮中类雌激素的双向调节作用，提高生猪产子数和蛋鸡产蛋率。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">作用机理<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">苜草素中的多糖、黄酮和皂苷等有效成分可以促进动物免疫器官的发育，增强<span>T</span>、<span>B</span>淋巴细胞的活化，提高血清抗体水平，从而显著增强机体的免疫功能，提高母源抗体水平。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">另外，苜草素可以降低脂肪合成酶和脂蛋白酯酶的活性，反而提高脂蛋白酯酶和解偶联蛋白的活性，从而改善了机体的脂肪代谢过程，降低了脂肪沉积，促进了肌肉发育，改善了畜产品质量和风味。<span>`</span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;">&nbsp;&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;font-family:宋体;">应用效果</span></b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;</span></b></b></p><p class="MsoNormal"><b><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"></span></b></b></b></b></p><p class="MsoNormal"><b><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;&nbsp;1</span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">、调节仔猪机体免疫功能，提高仔猪抗病能力，增强抗逆性，预防仔猪腹泻<span></span></span></b></b></b></b></p><p class="MsoNormal"><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">综合效果可替代抗生素。<span></span></span></b></b></b></p><p class="MsoNormal"><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;&nbsp;2</span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">、提高仔猪增重和饲料转化率<span>15%</span>，提高成活率<span>8%</span>。</span></b></b></b></p><p><br/></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;">使用方法</span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span style="font-size:16px;"><span style="line-height:24px;font-size:16px;">每吨全价料添加1000g进行拌料。</span></span></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/>保质期为<span>2</span>年。</span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p><br/></p><p><br/></p><p><span style="color:#006600;font-size:24px;">中国农业科学畜牧研究所检测报告</span></p><hr/><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;无公害饲料添加剂－"苜草素"是由中国农科院畜牧研究所研制，于2003年12月被批准为饲料添加剂新产品，新产品证书号为新饲证字(2003)06号。"苜草素"是一个具有我国自主知识产权的无公害天然饲料添加剂新产品，目前已经获得国家发明专利。</span></p><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过实践证明，在肉仔鸡饲养过程中添加"苜草素"，可以提高肉仔鸡平均日增重11.92%，提高饲料转化效率2.65%，降低腹脂14.7%，提高胸肌率8.5%，能够减少早期肉仔鸡腹泻，提高存活率9.3%；可以抑制肉仔鸡不同生长阶段肠道内大肠杆菌的繁殖，降低菌群数量。在同等饲养条件下，添加"苜草素"仔猪平均日增重提高5.5%，饲料转化效率提高1.3%，有效的提高了猪肉产品的质量，保持了猪肉产品天然的风味和口感。</span></p><p><span style="font-size:16px;">检测报告：</span></p><p><span style="font-size:16px;">中国医学科学院药用植物研究所</span><span style="line-height:1.5;font-size:16px;">检测报告</span></p><p><span style="line-height:1.5;font-size:16px;"><b><span style="font-size:16px;">1.</span></b><b><span style="font-size:16px;">样品基本信息</span></b><br/></span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检单位：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">中国农业科学院畜牧研究所</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测样品：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">样品数量：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;4</span></u><u><span style="font-size:14px;">份</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检日期：</span><u><span style="font-size:14px;">&nbsp;2014</span></u><u><span style="font-size:14px;">年</span></u><u><span style="font-size:14px;">7</span></u><u><span style="font-size:14px;">月</span></u><u><span style="font-size:14px;">15</span></u><u><span style="font-size:14px;">日</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测内容：</span><u><span style="font-size:14px;">&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素样品中总皂苷的含量检测</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">编号方法：</span><u><span style="font-size:14px;">&nbsp;4</span></u><u><span style="font-size:14px;">份送检样品分别编号为</span></u><u><span style="font-size:14px;">1-4</span></u><u><span style="font-size:14px;">，见图</span></u><u><span style="font-size:14px;">1&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><u><span style="font-size:14px;"><br/></span></u></p><p><!--[if gte vml 1]><![endif]--><!--[if!vml]--><img width="467"height="192"src="/Public/Uploads/image/201507/8typhzh.jpg"title="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"align=""alt="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"/></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">2.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测目标</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">利用分光光度法测定送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">3.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测方法</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:16px;line-height:150%;">3.1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">检测波长的确定</span></p><p class="MsoNormal"style="margin-left:18.0pt;text-align:justify;text-indent:-18.0pt;"><b><span style="font-size:12.0pt;line-height:150%;"><span style="font-size:16px;">4.</span><span style="font-weight:normal;font-size:16px;line-height:normal;font-family:''Times New Roman'';">&nbsp;</span></span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测结果</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量在</span><span style="font-size:16px;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:16px;line-height:150%;font-family:宋体;">之间，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品含量最高，</span><span style="font-size:16px;line-height:150%;">1-3</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品的总皂苷含量接近，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份样品间差异均不显著（表</span><span style="font-size:16px;line-height:150%;">1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">，</span><i><span style="font-size:16px;line-height:150%;">P</span></i><span style="font-size:16px;line-height:150%;">&gt;0.05</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量均低于杨江涛等</span><sup><span style="font-size:16px;line-height:150%;">[1]</span></sup><span style="font-size:16px;line-height:150%;font-family:宋体;">的报道（总皂苷含量</span><span style="font-size:16px;line-height:150%;">≥5%</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:12.0pt;line-height:150%;">5.</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要结论</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">本实验采用分光光度计测定苜草素中总皂苷的含量，标准曲线的线性关系良好（</span><i><span style="font-size:12.0pt;line-height:150%;">R</span></i><sup><span style="font-size:12.0pt;line-height:150%;">2</span></sup><span style="font-size:12.0pt;line-height:150%;">=1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">），检测</span><span style="font-size:12.0pt;line-height:150%;">4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量为</span><span style="font-size:12.0pt;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">。</span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"><br/></span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">主要参考文献：</span><span style="font-size:10.5pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;">[1]</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">杨江涛</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">董晓芳</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">佟建明</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">刘晴雪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">张琪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">吴莹莹</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">苜草素多糖、黄酮和皂苷对小鼠巨噬细胞</span><span style="font-size:10.5pt;line-height:150%;">RAW264.7B-</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">防御素基因表达的影响</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">畜牧兽医学报</span><span style="font-size:10.5pt;line-height:150%;">,2010,41(5):608–614.</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测操作人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">焦晓林</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测负责人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">高微微</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">报告日期：</span><span style="font-size:12.0pt;line-height:150%;">&nbsp;<u>2014</u></span><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">年</span></u><u><span style="font-size:12.0pt;line-height:150%;">7</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">月</span></u><u><span style="font-size:12.0pt;line-height:150%;">30</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">日</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:left;"><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><span><span style="line-height:24px;"><br/></span></span></p>', '1000克/袋', 0, 0, 0, 0, 0, 0);
INSERT INTO `l_product` VALUES (7, '苜草素7号 育肥猪专用  粉状 配料  1000g装', '蓝海洋科技', 0.00, '/public/uploads/product/7.jpg', '/public/uploads/product/thumb/7.jpg', '育肥猪 育肥猪专用 苜草素饲料添加剂 饲料添加剂  检测 标准 检测报告', '<p><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><img src="http://www.mucaosu.com/public/uploads/product/7_1.jpg"alt=""width="800"height="1183"title=""align=""/></p><p class="MsoNormal"align="center"style="text-align:center;"><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;"><img src="http://www.mucaosu.com/public/uploads/product/7_2.jpg"alt=""width="800"height="1232"title=""align=""/><br/></span></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;">苜草素7号产品说明书<span></span></span></p><hr/><p class="MsoNormal"style="text-indent:21.1pt;"><b><span style="line-height:150%;font-family:黑体;">&nbsp;</span></b><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">是一种以紫花苜蓿为主要原料的天然植物提取物，具有增强动物免疫机能、改善畜产品质量、促进畜禽生长的作用，综合效果可替代饲用抗生素，而且无毒副作用，是一种新型的绿色的有效保护畜禽健康的饲料添加剂。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要成份</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">的有效活性成分包括：</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">多糖≥<span>15%</span>、</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">黄酮</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">≥<span>5%</span>、皂甙≥<span>5%</span>。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">产品性状<span>&nbsp;</span></span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">棕黄色粉末，气味芳香。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要功能<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、提高母源抗体水平，增强幼龄动物的抵抗力。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;2</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、增强动物机体免疫力，促进生长。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;3</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、调节脂肪代谢，有效预防脂肪肝等营养代谢病。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、改善肉质和风味。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;5</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、替代饲用抗生素。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;<span style="color:blue;">&nbsp;6</span></span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:blue;">、通过苜蓿黄酮中类雌激素的双向调节作用，提高生猪产子数和蛋鸡产蛋率。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">作用机理<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">苜草素中的多糖、黄酮和皂苷等有效成分可以促进动物免疫器官的发育，增强<span>T</span>、<span>B</span>淋巴细胞的活化，提高血清抗体水平，从而显著增强机体的免疫功能，提高母源抗体水平。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">另外，苜草素可以降低脂肪合成酶和脂蛋白酯酶的活性，反而提高脂蛋白酯酶和解偶联蛋白的活性，从而改善了机体的脂肪代谢过程，降低了脂肪沉积，促进了肌肉发育，改善了畜产品质量和风味。<span>`</span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;">&nbsp;&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;font-family:宋体;">应用效果</span></b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;</span></b></b><b><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;</span></b></b></b></b></p><p class="MsoNormal"><b><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;&nbsp;1</span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">、调节育肥猪机体免疫功能，提高育肥猪自身的抗病力，增强抗逆性，综合</span></b></b></b></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">效果可替代饲用抗生素。</span></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;&nbsp;2</span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">、苜草素可提高育肥猪增重<span>15%</span>，提高育肥猪饲料转化率<span>15%</span>，降低死淘率<span>22%</span>。<span></span></span></b></p><p><br/></p><p class="MsoNormal"><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"></span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><span></span><span></span></span></b></b></b></p><p><br/></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;">使用方法</span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span style="font-size:16px;"><span style="line-height:24px;font-size:16px;">每吨全价料添加1000g进行拌料。</span></span></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/>保质期为<span>2</span>年。</span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p><br/></p><p><br/></p><p><span style="color:#006600;font-size:24px;">中国农业科学畜牧研究所检测报告</span></p><hr/><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;无公害饲料添加剂－"苜草素"是由中国农科院畜牧研究所研制，于2003年12月被批准为饲料添加剂新产品，新产品证书号为新饲证字(2003)06号。"苜草素"是一个具有我国自主知识产权的无公害天然饲料添加剂新产品，目前已经获得国家发明专利。</span></p><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过实践证明，在肉仔鸡饲养过程中添加"苜草素"，可以提高肉仔鸡平均日增重11.92%，提高饲料转化效率2.65%，降低腹脂14.7%，提高胸肌率8.5%，能够减少早期肉仔鸡腹泻，提高存活率9.3%；可以抑制肉仔鸡不同生长阶段肠道内大肠杆菌的繁殖，降低菌群数量。在同等饲养条件下，添加"苜草素"仔猪平均日增重提高5.5%，饲料转化效率提高1.3%，有效的提高了猪肉产品的质量，保持了猪肉产品天然的风味和口感。</span></p><p><span style="font-size:16px;">检测报告：</span></p><p><span style="font-size:16px;">中国医学科学院药用植物研究所</span><span style="line-height:1.5;font-size:16px;">检测报告</span></p><p><span style="line-height:1.5;font-size:16px;"><b><span style="font-size:16px;">1.</span></b><b><span style="font-size:16px;">样品基本信息</span></b><br/></span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检单位：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">中国农业科学院畜牧研究所</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测样品：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">样品数量：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;4</span></u><u><span style="font-size:14px;">份</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检日期：</span><u><span style="font-size:14px;">&nbsp;2014</span></u><u><span style="font-size:14px;">年</span></u><u><span style="font-size:14px;">7</span></u><u><span style="font-size:14px;">月</span></u><u><span style="font-size:14px;">15</span></u><u><span style="font-size:14px;">日</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测内容：</span><u><span style="font-size:14px;">&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素样品中总皂苷的含量检测</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">编号方法：</span><u><span style="font-size:14px;">&nbsp;4</span></u><u><span style="font-size:14px;">份送检样品分别编号为</span></u><u><span style="font-size:14px;">1-4</span></u><u><span style="font-size:14px;">，见图</span></u><u><span style="font-size:14px;">1&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><u><span style="font-size:14px;"><br/></span></u></p><p><!--[if gte vml 1]><![endif]--><!--[if!vml]--><img width="467"height="192"src="/Public/Uploads/image/201507/8typhzh.jpg"title="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"align=""alt="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"/></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">2.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测目标</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">利用分光光度法测定送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">3.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测方法</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:16px;line-height:150%;">3.1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">检测波长的确定</span></p><p class="MsoNormal"style="margin-left:18.0pt;text-align:justify;text-indent:-18.0pt;"><b><span style="font-size:12.0pt;line-height:150%;"><span style="font-size:16px;">4.</span><span style="font-weight:normal;font-size:16px;line-height:normal;font-family:''Times New Roman'';">&nbsp;</span></span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测结果</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量在</span><span style="font-size:16px;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:16px;line-height:150%;font-family:宋体;">之间，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品含量最高，</span><span style="font-size:16px;line-height:150%;">1-3</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品的总皂苷含量接近，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份样品间差异均不显著（表</span><span style="font-size:16px;line-height:150%;">1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">，</span><i><span style="font-size:16px;line-height:150%;">P</span></i><span style="font-size:16px;line-height:150%;">&gt;0.05</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量均低于杨江涛等</span><sup><span style="font-size:16px;line-height:150%;">[1]</span></sup><span style="font-size:16px;line-height:150%;font-family:宋体;">的报道（总皂苷含量</span><span style="font-size:16px;line-height:150%;">≥5%</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:12.0pt;line-height:150%;">5.</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要结论</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">本实验采用分光光度计测定苜草素中总皂苷的含量，标准曲线的线性关系良好（</span><i><span style="font-size:12.0pt;line-height:150%;">R</span></i><sup><span style="font-size:12.0pt;line-height:150%;">2</span></sup><span style="font-size:12.0pt;line-height:150%;">=1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">），检测</span><span style="font-size:12.0pt;line-height:150%;">4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量为</span><span style="font-size:12.0pt;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">。</span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"><br/></span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">主要参考文献：</span><span style="font-size:10.5pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;">[1]</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">杨江涛</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">董晓芳</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">佟建明</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">刘晴雪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">张琪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">吴莹莹</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">苜草素多糖、黄酮和皂苷对小鼠巨噬细胞</span><span style="font-size:10.5pt;line-height:150%;">RAW264.7B-</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">防御素基因表达的影响</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">畜牧兽医学报</span><span style="font-size:10.5pt;line-height:150%;">,2010,41(5):608–614.</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测操作人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">焦晓林</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测负责人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">高微微</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">报告日期：</span><span style="font-size:12.0pt;line-height:150%;">&nbsp;<u>2014</u></span><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">年</span></u><u><span style="font-size:12.0pt;line-height:150%;">7</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">月</span></u><u><span style="font-size:12.0pt;line-height:150%;">30</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">日</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:left;"><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><span><span style="line-height:24px;"><br/></span></span></p>', '1000克/袋', 0, 0, 0, 0, 0, 0);
INSERT INTO `l_product` VALUES (8, '苜草素8号 妊娠母猪专用  颗粒 直喂 ', '蓝海洋科技', 0.00, '/public/uploads/product/8.jpg', '/public/uploads/product/thumb/8.jpg', '妊娠母猪 妊娠母猪专用 苜草素饲料添加剂  饲料添加剂 检测 标准 检测报告', '<p><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><img src="http://www.mucaosu.com/public/uploads/product/8_1.jpg"alt=""width="800"height="1187"title=""align=""/></p><p class="MsoNormal"align="center"style="text-align:center;"><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;"><img src="http://www.mucaosu.com/public/uploads/product/8_2.jpg"alt=""width="800"height="1174"title=""align=""/><br/></span></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;">苜草素8号产品说明书<span></span></span></p><hr/><p class="MsoNormal"style="text-indent:21.1pt;"><b><span style="line-height:150%;font-family:黑体;">&nbsp;</span></b><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">是一种以紫花苜蓿为主要原料的天然植物提取物，具有增强动物免疫机能、改善畜产品质量、促进畜禽生长的作用，综合效果可替代饲用抗生素，而且无毒副作用，是一种新型的绿色的有效保护畜禽健康的饲料添加剂。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要成份</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">的有效活性成分包括：</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">多糖≥<span>15%</span>、</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">黄酮</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">≥<span>5%</span>、皂甙≥<span>5%</span>。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">产品性状<span>&nbsp;</span></span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">棕黄色粉末，气味芳香。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要功能<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、提高母源抗体水平，增强幼龄动物的抵抗力。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;2</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、增强动物机体免疫力，促进生长。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;3</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、调节脂肪代谢，有效预防脂肪肝等营养代谢病。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、改善肉质和风味。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;5</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、替代饲用抗生素。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;<span style="color:blue;">&nbsp;6</span></span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:blue;">、通过苜蓿黄酮中类雌激素的双向调节作用，提高生猪产子数和蛋鸡产蛋率。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">作用机理<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">苜草素中的多糖、黄酮和皂苷等有效成分可以促进动物免疫器官的发育，增强<span>T</span>、<span>B</span>淋巴细胞的活化，提高血清抗体水平，从而显著增强机体的免疫功能，提高母源抗体水平。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">另外，苜草素可以降低脂肪合成酶和脂蛋白酯酶的活性，反而提高脂蛋白酯酶和解偶联蛋白的活性，从而改善了机体的脂肪代谢过程，降低了脂肪沉积，促进了肌肉发育，改善了畜产品质量和风味。<span>`</span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;">&nbsp;&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;font-family:宋体;">应用效果</span></b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;</span></b></b></p><p class="MsoNormal"><b><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;&nbsp;1</span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">、调节妊娠母猪机体免疫功能，提高妊娠母猪自身的抗病力，增强抗逆性，</span></b><b><b><b><b><span style="font-size:12pt;font-family:宋体;"></span></b></b></b></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">预防产后仔猪腹泻，综合效果可替代饲用抗生素。</span></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;2</span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">、苜草素可提高产仔率<span>10%</span>，提高仔猪成活率<span>6%</span>，提高饲料转化率<span>15%</span>，降低</span></b><b><b><b><b><span style="font-size:12pt;font-family:宋体;"></span></b></b></b></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">死淘率<span>20%</span>。</span></b></p></span></b></b></b></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;"></span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><span></span><span></span><span></span><span></span></span></b></p><p><br/></p><p class="MsoNormal"><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"></span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><span></span><span></span></span></b></b></b></p><p><br/></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;">使用方法</span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span style="font-size:16px;"><span style="line-height:24px;font-size:16px;">直接投入料槽中饲喂</span></span></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/>保质期为<span>2</span>年。</span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p><br/></p><p><br/></p><p><span style="color:#006600;font-size:24px;">中国农业科学畜牧研究所检测报告</span></p><hr/><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;无公害饲料添加剂－"苜草素"是由中国农科院畜牧研究所研制，于2003年12月被批准为饲料添加剂新产品，新产品证书号为新饲证字(2003)06号。"苜草素"是一个具有我国自主知识产权的无公害天然饲料添加剂新产品，目前已经获得国家发明专利。</span></p><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过实践证明，在肉仔鸡饲养过程中添加"苜草素"，可以提高肉仔鸡平均日增重11.92%，提高饲料转化效率2.65%，降低腹脂14.7%，提高胸肌率8.5%，能够减少早期肉仔鸡腹泻，提高存活率9.3%；可以抑制肉仔鸡不同生长阶段肠道内大肠杆菌的繁殖，降低菌群数量。在同等饲养条件下，添加"苜草素"仔猪平均日增重提高5.5%，饲料转化效率提高1.3%，有效的提高了猪肉产品的质量，保持了猪肉产品天然的风味和口感。</span></p><p><span style="font-size:16px;">检测报告：</span></p><p><span style="font-size:16px;">中国医学科学院药用植物研究所</span><span style="line-height:1.5;font-size:16px;">检测报告</span></p><p><span style="line-height:1.5;font-size:16px;"><b><span style="font-size:16px;">1.</span></b><b><span style="font-size:16px;">样品基本信息</span></b><br/></span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检单位：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">中国农业科学院畜牧研究所</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测样品：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">样品数量：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;4</span></u><u><span style="font-size:14px;">份</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检日期：</span><u><span style="font-size:14px;">&nbsp;2014</span></u><u><span style="font-size:14px;">年</span></u><u><span style="font-size:14px;">7</span></u><u><span style="font-size:14px;">月</span></u><u><span style="font-size:14px;">15</span></u><u><span style="font-size:14px;">日</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测内容：</span><u><span style="font-size:14px;">&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素样品中总皂苷的含量检测</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">编号方法：</span><u><span style="font-size:14px;">&nbsp;4</span></u><u><span style="font-size:14px;">份送检样品分别编号为</span></u><u><span style="font-size:14px;">1-4</span></u><u><span style="font-size:14px;">，见图</span></u><u><span style="font-size:14px;">1&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><u><span style="font-size:14px;"><br/></span></u></p><p><!--[if gte vml 1]><![endif]--><!--[if!vml]--><img width="467"height="192"src="/Public/Uploads/image/201507/8typhzh.jpg"title="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"align=""alt="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"/></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">2.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测目标</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">利用分光光度法测定送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">3.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测方法</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:16px;line-height:150%;">3.1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">检测波长的确定</span></p><p class="MsoNormal"style="margin-left:18.0pt;text-align:justify;text-indent:-18.0pt;"><b><span style="font-size:12.0pt;line-height:150%;"><span style="font-size:16px;">4.</span><span style="font-weight:normal;font-size:16px;line-height:normal;font-family:''Times New Roman'';">&nbsp;</span></span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测结果</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量在</span><span style="font-size:16px;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:16px;line-height:150%;font-family:宋体;">之间，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品含量最高，</span><span style="font-size:16px;line-height:150%;">1-3</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品的总皂苷含量接近，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份样品间差异均不显著（表</span><span style="font-size:16px;line-height:150%;">1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">，</span><i><span style="font-size:16px;line-height:150%;">P</span></i><span style="font-size:16px;line-height:150%;">&gt;0.05</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量均低于杨江涛等</span><sup><span style="font-size:16px;line-height:150%;">[1]</span></sup><span style="font-size:16px;line-height:150%;font-family:宋体;">的报道（总皂苷含量</span><span style="font-size:16px;line-height:150%;">≥5%</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:12.0pt;line-height:150%;">5.</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要结论</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">本实验采用分光光度计测定苜草素中总皂苷的含量，标准曲线的线性关系良好（</span><i><span style="font-size:12.0pt;line-height:150%;">R</span></i><sup><span style="font-size:12.0pt;line-height:150%;">2</span></sup><span style="font-size:12.0pt;line-height:150%;">=1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">），检测</span><span style="font-size:12.0pt;line-height:150%;">4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量为</span><span style="font-size:12.0pt;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">。</span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"><br/></span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">主要参考文献：</span><span style="font-size:10.5pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;">[1]</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">杨江涛</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">董晓芳</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">佟建明</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">刘晴雪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">张琪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">吴莹莹</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">苜草素多糖、黄酮和皂苷对小鼠巨噬细胞</span><span style="font-size:10.5pt;line-height:150%;">RAW264.7B-</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">防御素基因表达的影响</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">畜牧兽医学报</span><span style="font-size:10.5pt;line-height:150%;">,2010,41(5):608–614.</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测操作人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">焦晓林</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测负责人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">高微微</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">报告日期：</span><span style="font-size:12.0pt;line-height:150%;">&nbsp;<u>2014</u></span><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">年</span></u><u><span style="font-size:12.0pt;line-height:150%;">7</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">月</span></u><u><span style="font-size:12.0pt;line-height:150%;">30</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">日</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:left;"><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><span><span style="line-height:24px;"><br/></span></span></p>', '10克*50袋*18大袋/件', 0, 0, 0, 0, 0, 0);
INSERT INTO `l_product` VALUES (9, '苜草素9号 哺乳母猪专用  颗粒 直喂 ', '蓝海洋科技', 0.00, '/public/uploads/product/9.jpg', '/public/uploads/product/thumb/9.jpg', '哺乳母猪 哺乳母猪专用 苜草素饲料添加剂 饲料添加剂 检测 标准 检测报告', '<p><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><img src="http://www.mucaosu.com/public/uploads/product/9_1.jpg"alt=""width="800"height="1187"title=""align=""/></p><p class="MsoNormal"align="center"style="text-align:center;"><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;"><img src="http://www.mucaosu.com/public/uploads/product/9_2.jpg"alt=""width="800"height="1174"title=""align=""/><br/></span></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;">苜草素9号产品说明书<span></span></span></p><hr/><p class="MsoNormal"style="text-indent:21.1pt;"><b><span style="line-height:150%;font-family:黑体;">&nbsp;</span></b><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">是一种以紫花苜蓿为主要原料的天然植物提取物，具有增强动物免疫机能、改善畜产品质量、促进畜禽生长的作用，综合效果可替代饲用抗生素，而且无毒副作用，是一种新型的绿色的有效保护畜禽健康的饲料添加剂。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要成份</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">的有效活性成分包括：</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">多糖≥<span>15%</span>、</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">黄酮</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">≥<span>5%</span>、皂甙≥<span>5%</span>。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">产品性状<span>&nbsp;</span></span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">棕黄色粉末，气味芳香。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要功能<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、提高母源抗体水平，增强幼龄动物的抵抗力。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;2</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、增强动物机体免疫力，促进生长。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;3</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、调节脂肪代谢，有效预防脂肪肝等营养代谢病。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、改善肉质和风味。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;5</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、替代饲用抗生素。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;<span style="color:blue;">&nbsp;6</span></span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:blue;">、通过苜蓿黄酮中类雌激素的双向调节作用，提高生猪产子数和蛋鸡产蛋率。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">作用机理<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">苜草素中的多糖、黄酮和皂苷等有效成分可以促进动物免疫器官的发育，增强<span>T</span>、<span>B</span>淋巴细胞的活化，提高血清抗体水平，从而显著增强机体的免疫功能，提高母源抗体水平。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">另外，苜草素可以降低脂肪合成酶和脂蛋白酯酶的活性，反而提高脂蛋白酯酶和解偶联蛋白的活性，从而改善了机体的脂肪代谢过程，降低了脂肪沉积，促进了肌肉发育，改善了畜产品质量和风味。<span>`</span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;">&nbsp;&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;font-family:宋体;">应用效果</span></b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;</span></b></b><b><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;</span></b></b></b></b></p><p class="MsoNormal"><b><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;&nbsp;1</span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">、增强产后母猪免疫机能，提高产后母猪的抗病力，增强抗逆性，保证母乳</span></b><b><b><b><b><span style="font-size:12pt;font-family:宋体;"></span></b></b></b></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">充足，预防产后仔猪腹泻，综合效果可替代抗生素。</span></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;&nbsp;2</span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">、可有效提高仔猪成活率<span>6%</span>，提高母猪饲料转化率<span>15%</span>，提高配种率<span>17%</span>。<span></span></span></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;"></span></b></p></span></b></b></b></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><span></span></span></b></p><p><br/></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;"></span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><span></span><span></span><span></span><span></span></span></b></p><p><br/></p><p class="MsoNormal"><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"></span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><span></span><span></span></span></b></b></b></p><p><br/></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;">使用方法</span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span style="font-size:16px;"><span style="line-height:24px;font-size:16px;">直接投入料槽中饲喂</span></span></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/>保质期为<span>2</span>年。</span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p><br/></p><p><br/></p><p><span style="color:#006600;font-size:24px;">中国农业科学畜牧研究所检测报告</span></p><hr/><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;无公害饲料添加剂－"苜草素"是由中国农科院畜牧研究所研制，于2003年12月被批准为饲料添加剂新产品，新产品证书号为新饲证字(2003)06号。"苜草素"是一个具有我国自主知识产权的无公害天然饲料添加剂新产品，目前已经获得国家发明专利。</span></p><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过实践证明，在肉仔鸡饲养过程中添加"苜草素"，可以提高肉仔鸡平均日增重11.92%，提高饲料转化效率2.65%，降低腹脂14.7%，提高胸肌率8.5%，能够减少早期肉仔鸡腹泻，提高存活率9.3%；可以抑制肉仔鸡不同生长阶段肠道内大肠杆菌的繁殖，降低菌群数量。在同等饲养条件下，添加"苜草素"仔猪平均日增重提高5.5%，饲料转化效率提高1.3%，有效的提高了猪肉产品的质量，保持了猪肉产品天然的风味和口感。</span></p><p><span style="font-size:16px;">检测报告：</span></p><p><span style="font-size:16px;">中国医学科学院药用植物研究所</span><span style="line-height:1.5;font-size:16px;">检测报告</span></p><p><span style="line-height:1.5;font-size:16px;"><b><span style="font-size:16px;">1.</span></b><b><span style="font-size:16px;">样品基本信息</span></b><br/></span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检单位：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">中国农业科学院畜牧研究所</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测样品：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">样品数量：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;4</span></u><u><span style="font-size:14px;">份</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检日期：</span><u><span style="font-size:14px;">&nbsp;2014</span></u><u><span style="font-size:14px;">年</span></u><u><span style="font-size:14px;">7</span></u><u><span style="font-size:14px;">月</span></u><u><span style="font-size:14px;">15</span></u><u><span style="font-size:14px;">日</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测内容：</span><u><span style="font-size:14px;">&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素样品中总皂苷的含量检测</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">编号方法：</span><u><span style="font-size:14px;">&nbsp;4</span></u><u><span style="font-size:14px;">份送检样品分别编号为</span></u><u><span style="font-size:14px;">1-4</span></u><u><span style="font-size:14px;">，见图</span></u><u><span style="font-size:14px;">1&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><u><span style="font-size:14px;"><br/></span></u></p><p><!--[if gte vml 1]><![endif]--><!--[if!vml]--><img width="467"height="192"src="/Public/Uploads/image/201507/8typhzh.jpg"title="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"align=""alt="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"/></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">2.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测目标</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">利用分光光度法测定送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">3.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测方法</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:16px;line-height:150%;">3.1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">检测波长的确定</span></p><p class="MsoNormal"style="margin-left:18.0pt;text-align:justify;text-indent:-18.0pt;"><b><span style="font-size:12.0pt;line-height:150%;"><span style="font-size:16px;">4.</span><span style="font-weight:normal;font-size:16px;line-height:normal;font-family:''Times New Roman'';">&nbsp;</span></span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测结果</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量在</span><span style="font-size:16px;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:16px;line-height:150%;font-family:宋体;">之间，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品含量最高，</span><span style="font-size:16px;line-height:150%;">1-3</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品的总皂苷含量接近，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份样品间差异均不显著（表</span><span style="font-size:16px;line-height:150%;">1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">，</span><i><span style="font-size:16px;line-height:150%;">P</span></i><span style="font-size:16px;line-height:150%;">&gt;0.05</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量均低于杨江涛等</span><sup><span style="font-size:16px;line-height:150%;">[1]</span></sup><span style="font-size:16px;line-height:150%;font-family:宋体;">的报道（总皂苷含量</span><span style="font-size:16px;line-height:150%;">≥5%</span><span style="font-size:16px;line-height:150%;font-family:宋体;">）。</span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:12.0pt;line-height:150%;">5.</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要结论</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">本实验采用分光光度计测定苜草素中总皂苷的含量，标准曲线的线性关系良好（</span><i><span style="font-size:12.0pt;line-height:150%;">R</span></i><sup><span style="font-size:12.0pt;line-height:150%;">2</span></sup><span style="font-size:12.0pt;line-height:150%;">=1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">），检测</span><span style="font-size:12.0pt;line-height:150%;">4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量为</span><span style="font-size:12.0pt;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">。</span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"><br/></span></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">主要参考文献：</span><span style="font-size:10.5pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:10.5pt;line-height:150%;">[1]</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">杨江涛</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">董晓芳</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">佟建明</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">刘晴雪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">张琪</span><span style="font-size:10.5pt;line-height:150%;">,</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">吴莹莹</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">苜草素多糖、黄酮和皂苷对小鼠巨噬细胞</span><span style="font-size:10.5pt;line-height:150%;">RAW264.7B-</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">防御素基因表达的影响</span><span style="font-size:10.5pt;line-height:150%;">.</span><span style="font-size:10.5pt;line-height:150%;font-family:宋体;">畜牧兽医学报</span><span style="font-size:10.5pt;line-height:150%;">,2010,41(5):608–614.</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测操作人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">焦晓林</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">检测负责人：</span><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">高微微</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:right;text-indent:210pt;"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">报告日期：</span><span style="font-size:12.0pt;line-height:150%;">&nbsp;<u>2014</u></span><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">年</span></u><u><span style="font-size:12.0pt;line-height:150%;">7</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">月</span></u><u><span style="font-size:12.0pt;line-height:150%;">30</span></u><u><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">日</span></u><u><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="text-align:left;"><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><span><span style="line-height:24px;"><br/></span></span></p>', '10克*50袋*18大袋/件', 0, 0, 0, 0, 0, 0);
INSERT INTO `l_product` VALUES (10, '苜草素10号 保育仔猪专用  颗粒 直喂 ', '蓝海洋科技', 0.00, '/public/uploads/product/10.jpg', '/public/uploads/product/thumb/10.jpg', '保育仔猪 保育仔猪专用 苜草素饲料添加剂 饲料添加剂 检测 标准 检测报告', '<p><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><img src="http://www.mucaosu.com/public/uploads/product/10_1.jpg"alt=""width="800"height="1187"title=""align=""/></p><p class="MsoNormal"align="center"style="text-align:center;"><br/></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;"><img src="http://www.mucaosu.com/public/uploads/product/10_2.jpg"alt=""width="800"height="1174"title=""align=""/><br/></span></p><p class="MsoNormal"align="center"style="text-align:center;"><span style="font-size:26.0pt;font-family:黑体;color:green;">苜草素10号产品说明书<span></span></span></p><hr/><p class="MsoNormal"style="text-indent:21.1pt;"><b><span style="line-height:150%;font-family:黑体;">&nbsp;</span></b><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">是一种以紫花苜蓿为主要原料的天然植物提取物，具有增强动物免疫机能、改善畜产品质量、促进畜禽生长的作用，综合效果可替代饲用抗生素，而且无毒副作用，是一种新型的绿色的有效保护畜禽健康的饲料添加剂。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要成份</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">苜草素</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">的有效活性成分包括：</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">多糖≥<span>15%</span>、</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">黄酮</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">≥<span>5%</span>、皂甙≥<span>5%</span>。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">产品性状<span>&nbsp;</span></span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">棕黄色粉末，气味芳香。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">主要功能<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;1</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、提高母源抗体水平，增强幼龄动物的抵抗力。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;2</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、增强动物机体免疫力，促进生长。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;3</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、调节脂肪代谢，有效预防脂肪肝等营养代谢病。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;4</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、改善肉质和风味。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;5</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">、替代饲用抗生素。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;<span style="color:blue;">&nbsp;6</span></span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:blue;">、通过苜蓿黄酮中类雌激素的双向调节作用，提高生猪产子数和蛋鸡产蛋率。<span></span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">作用机理<span></span></span></b></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:黑体;color:green;">&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">苜草素中的多糖、黄酮和皂苷等有效成分可以促进动物免疫器官的发育，增强<span>T</span>、<span>B</span>淋巴细胞的活化，提高血清抗体水平，从而显著增强机体的免疫功能，提高母源抗体水平。<span></span></span></p><p class="MsoNormal"><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">&nbsp;&nbsp;&nbsp;</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">另外，苜草素可以降低脂肪合成酶和脂蛋白酯酶的活性，反而提高脂蛋白酯酶和解偶联蛋白的活性，从而改善了机体的脂肪代谢过程，降低了脂肪沉积，促进了肌肉发育，改善了畜产品质量和风味。<span>`</span></span></p><p class="MsoNormal"><b><span style="font-size:12.0pt;">&nbsp;&nbsp;&nbsp;</span></b><b><span style="font-size:12.0pt;font-family:宋体;">应用效果</span></b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;</span></b></b><b><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;</span></b></b></b></b><b><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;</span></b></b></b></b></p><p class="MsoNormal"><b><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;&nbsp;1</span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">、调节仔猪机体免疫功能，提高仔猪抗病能力，增强抗逆性，预防仔猪腹泻综合效果可替代抗生素。<span></span></span></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;">&nbsp;&nbsp;&nbsp;&nbsp;2</span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;">、提高仔猪增重和饲料转化率<span>15%</span>，提高成活率<span>8%</span>。<span></span></span></b></p></span></b></b></b></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;"></span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><span></span><span></span><span></span><span></span></span></b></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;"></span></b></p><p><br/></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><span></span></span></b></p><p><br/></p><p class="MsoNormal"><b><span style="font-size:12.0pt;font-family:宋体;color:green;"></span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><span></span><span></span><span></span><span></span></span></b></p><p><br/></p><p class="MsoNormal"><b><b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"></span></b><b><span style="font-size:12.0pt;font-family:宋体;color:green;"><span></span><span></span></span></b></b></b></p><p><br/></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;">使用方法</span></b><span style="font-size:12.0pt;line-height:150%;font-family:宋体;"></span></p><p class="MsoNormal"align="left"style="text-indent:23.5pt;"><span style="font-size:16px;"><span style="line-height:24px;font-size:16px;">直接投入料槽中饲喂</span></span></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/>保质期为<span>2</span>年。</span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p class="MsoNormal"align="left"style="text-indent:23.6pt;"><b><span style="font-size:12pt;line-height:150%;font-family:宋体;"><br/></span></b></p><p><br/></p><p><br/></p><p><span style="color:#006600;font-size:24px;">中国农业科学畜牧研究所检测报告</span></p><hr/><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;无公害饲料添加剂－"苜草素"是由中国农科院畜牧研究所研制，于2003年12月被批准为饲料添加剂新产品，新产品证书号为新饲证字(2003)06号。"苜草素"是一个具有我国自主知识产权的无公害天然饲料添加剂新产品，目前已经获得国家发明专利。</span></p><p><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过实践证明，在肉仔鸡饲养过程中添加"苜草素"，可以提高肉仔鸡平均日增重11.92%，提高饲料转化效率2.65%，降低腹脂14.7%，提高胸肌率8.5%，能够减少早期肉仔鸡腹泻，提高存活率9.3%；可以抑制肉仔鸡不同生长阶段肠道内大肠杆菌的繁殖，降低菌群数量。在同等饲养条件下，添加"苜草素"仔猪平均日增重提高5.5%，饲料转化效率提高1.3%，有效的提高了猪肉产品的质量，保持了猪肉产品天然的风味和口感。</span></p><p><span style="font-size:16px;">检测报告：</span></p><p><span style="font-size:16px;">中国医学科学院药用植物研究所</span><span style="line-height:1.5;font-size:16px;">检测报告</span></p><p><span style="line-height:1.5;font-size:16px;"><b><span style="font-size:16px;">1.</span></b><b><span style="font-size:16px;">样品基本信息</span></b><br/></span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检单位：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">中国农业科学院畜牧研究所</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测样品：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">样品数量：</span><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;4</span></u><u><span style="font-size:14px;">份</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">送检日期：</span><u><span style="font-size:14px;">&nbsp;2014</span></u><u><span style="font-size:14px;">年</span></u><u><span style="font-size:14px;">7</span></u><u><span style="font-size:14px;">月</span></u><u><span style="font-size:14px;">15</span></u><u><span style="font-size:14px;">日</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">检测内容：</span><u><span style="font-size:14px;">&nbsp;&nbsp;</span></u><u><span style="font-size:14px;">苜草素样品中总皂苷的含量检测</span></u><u><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><u></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><span style="font-size:14px;">编号方法：</span><u><span style="font-size:14px;">&nbsp;4</span></u><u><span style="font-size:14px;">份送检样品分别编号为</span></u><u><span style="font-size:14px;">1-4</span></u><u><span style="font-size:14px;">，见图</span></u><u><span style="font-size:14px;">1&nbsp;</span></u></p><p class="MsoNormal"style="font-family:''sans serif'', tahoma, verdana, helvetica;text-align:justify;"><u><span style="font-size:14px;"><br/></span></u></p><p><!--[if gte vml 1]><![endif]--><!--[if!vml]--><img width="467"height="192"src="/Public/Uploads/image/201507/8typhzh.jpg"title="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"align=""alt="利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。"/></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">2.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测目标</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">利用分光光度法测定送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品中总皂苷的含量。</span><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:12.0pt;line-height:150%;"></span></p><p class="MsoNormal"style="text-align:justify;"><b><span style="font-size:16px;line-height:150%;">3.</span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测方法</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;"><span style="font-size:16px;line-height:150%;">3.1</span><span style="font-size:16px;line-height:150%;font-family:宋体;">检测波长的确定</span></p><p class="MsoNormal"style="margin-left:18.0pt;text-align:justify;text-indent:-18.0pt;"><b><span style="font-size:12.0pt;line-height:150%;"><span style="font-size:16px;">4.</span><span style="font-weight:normal;font-size:16px;line-height:normal;font-family:''Times New Roman'';">&nbsp;</span></span></b><b><span style="font-size:16px;line-height:150%;font-family:黑体;">检测结果</span></b><b><span style="font-size:12.0pt;line-height:150%;"></span></b></p><p class="MsoNormal"style="text-align:justify;text-indent:24.0pt;"><span style="font-size:16px;line-height:150%;font-family:宋体;">送检单位提供的</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">份苜草素样品总皂苷的含量在</span><span style="font-size:16px;line-height:150%;">36.29-41.44 mg/g</span><span style="font-size:16px;line-height:150%;font-family:宋体;">之间，</span><span style="font-size:16px;line-height:150%;">4</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品含量最高，</span><span style="font-size:16px;line-height:150%;">1-3</span><span style="font-size:16px;line-height:150%;font-family:宋体;">号样品的总皂苷含量接近，</span><span style="font-size:16px;line-height:150%;">4</span><span 