INSERT INTO `l_product` VALUES ('1', '苜草素10Kg', '蓝海洋科技', '0.00', '/public/uploads/index/p10kg.jpg', '/public/uploads/index/p10kg.jpg', '苜草素 10kg 检测 标准 检测报告', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 无公害饲料添加剂－\"苜草素\"是由中国农科院畜牧研究所研制，于2003年12月被批准为饲料添加剂新产品，新产品证书号为新饲证字(2003)06号。\"苜草素\"是一个具有我国自主知识产权的无公害天然饲料添加剂新产品，目前已经获得国家发明专利。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过实践证明，在肉仔鸡饲养过程中添加\"苜草素\"，可以提高肉仔鸡平均日增重11.92%，提高饲料转化效率2.65%，降低腹脂14.7%，提高胸肌率8.5%，能够减少早期肉仔鸡腹泻，提高存活率9.3%；可以抑制肉仔鸡不同生长阶段肠道内大肠杆菌的繁殖，降低菌群数量。在同等饲养条件下，添加\"苜草素\"仔猪平均日增重提高5.5%，饲料转化效率提高1.3%，有效的提高了猪肉产品的质量，保持了猪肉产品天然的风味和口感。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">检测报告：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">中国医学科学院药用植物研究所</span><span style=\"line-height:1.5;font-size:16px;\">检测报告</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-size:16px;\"><b><span style=\"font-size:16px;\">1.</span></b><b><span style=\"font-size:16px;\">样品基本信息</span></b><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-family:\'sans serif\', tahoma, verdana, helvetica;text-align:justify;\">\r\n	<span style=\"font-size:14px;\">送检单位：</span><u><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;</span></u><u><span style=\"font-size:14px;\">中国农业科学院畜牧研究所</span></u><u><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-family:\'sans serif\', tahoma, verdana, helvetica;text-align:justify;\">\r\n	<span style=\"font-size:14px;\">检测样品：</span><u><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;</span></u><u><span style=\"font-size:14px;\">苜草素</span></u><u><span style=\"font-size:14px;\">&nbsp; &nbsp;&nbsp;</span></u><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-family:\'sans serif\', tahoma, verdana, helvetica;text-align:justify;\">\r\n	<span style=\"font-size:14px;\">样品数量：</span><u><span style=\"font-size:14px;\">&nbsp; &nbsp;&nbsp;4</span></u><u><span style=\"font-size:14px;\">份</span></u><u><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-family:\'sans serif\', tahoma, verdana, helvetica;text-align:justify;\">\r\n	<span style=\"font-size:14px;\">送检日期：</span><u><span style=\"font-size:14px;\">&nbsp; 2014</span></u><u><span style=\"font-size:14px;\">年</span></u><u><span style=\"font-size:14px;\">7</span></u><u><span style=\"font-size:14px;\">月</span></u><u><span style=\"font-size:14px;\">15</span></u><u><span style=\"font-size:14px;\">日</span></u><u><span style=\"font-size:14px;\">&nbsp;&nbsp; &nbsp;&nbsp;</span></u><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><u></u> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-family:\'sans serif\', tahoma, verdana, helvetica;text-align:justify;\">\r\n	<span style=\"font-size:14px;\">检测内容：</span><u><span style=\"font-size:14px;\">&nbsp;&nbsp;</span></u><u><span style=\"font-size:14px;\">苜草素样品中总皂苷的含量检测</span></u><u><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span></u><u></u> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-family:\'sans serif\', tahoma, verdana, helvetica;text-align:justify;\">\r\n	<span style=\"font-size:14px;\">编号方法：</span><u><span style=\"font-size:14px;\">&nbsp; 4</span></u><u><span style=\"font-size:14px;\">份送检样品分别编号为</span></u><u><span style=\"font-size:14px;\">1-4</span></u><u><span style=\"font-size:14px;\">，见图</span></u><u><span style=\"font-size:14px;\">1&nbsp;</span></u> \r\n</p>\r\n<p>\r\n	<!--[if gte vml 1]><![endif]--><!--[if !vml]--><img width=\"467\" height=\"192\" src=\"/Public/Uploads/image/201507/8typhzh.jpg\" title=\"利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。\" align=\"\" alt=\"利用分光光度法测定送检单位提供的4份苜草素样品中总皂苷的含量。\" /> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<b><span style=\"font-size:16px;line-height:150%;\">2. </span></b><b><span style=\"font-size:16px;line-height:150%;font-family:黑体;\">检测目标</span></b><b><span style=\"font-size:12.0pt;line-height:150%;\"></span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:24.0pt;\">\r\n	<span style=\"font-size:16px;line-height:150%;font-family:宋体;\">利用分光光度法测定送检单位提供的</span><span style=\"font-size:16px;line-height:150%;\">4</span><span style=\"font-size:16px;line-height:150%;font-family:宋体;\">份苜草素样品中总皂苷的含量。</span><span style=\"font-size:12.0pt;line-height:150%;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:12.0pt;line-height:150%;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<b><span style=\"font-size:16px;line-height:150%;\">3. </span></b><b><span style=\"font-size:16px;line-height:150%;font-family:黑体;\">检测方法</span></b><b><span style=\"font-size:12.0pt;line-height:150%;\"></span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:16px;line-height:150%;\">3.1 </span><span style=\"font-size:16px;line-height:150%;font-family:宋体;\">检测波长的确定</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:18.0pt;text-align:justify;text-indent:-18.0pt;\">\r\n	<b><span style=\"font-size:12.0pt;line-height:150%;\"><span style=\"font-size:16px;\">4.</span><span style=\"font-weight:normal;font-size:16px;line-height:normal;font-family:\'Times New Roman\';\">&nbsp;</span></span></b><b><span style=\"font-size:16px;line-height:150%;font-family:黑体;\">检测结果</span></b><b><span style=\"font-size:12.0pt;line-height:150%;\"></span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:24.0pt;\">\r\n	<span style=\"font-size:16px;line-height:150%;font-family:宋体;\">送检单位提供的</span><span style=\"font-size:16px;line-height:150%;\">4</span><span style=\"font-size:16px;line-height:150%;font-family:宋体;\">份苜草素样品总皂苷的含量在</span><span style=\"font-size:16px;line-height:150%;\">36.29-41.44 mg/g</span><span style=\"font-size:16px;line-height:150%;font-family:宋体;\">之间，</span><span style=\"font-size:16px;line-height:150%;\">4</span><span style=\"font-size:16px;line-height:150%;font-family:宋体;\">号样品含量最高，</span><span style=\"font-size:16px;line-height:150%;\">1-3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体;\">号样品的总皂苷含量接近，</span><span style=\"font-size:16px;line-height:150%;\">4</span><span style=\"font-size:16px;line-height:150%;font-family:宋体;\">份样品间差异均不显著（表</span><span style=\"font-size:16px;line-height:150%;\">1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体;\">，</span><i><span style=\"font-size:16px;line-height:150%;\">P</span></i><span style=\"font-size:16px;line-height:150%;\">&gt;0.05</span><span style=\"font-size:16px;line-height:150%;font-family:宋体;\">）。</span><span style=\"font-size:16px;line-height:150%;\">4</span><span style=\"font-size:16px;line-height:150%;font-family:宋体;\">份苜草素样品总皂苷的含量均低于杨江涛等</span><sup><span style=\"font-size:16px;line-height:150%;\">[1]</span></sup><span style=\"font-size:16px;line-height:150%;font-family:宋体;\">的报道（总皂苷含量</span><span style=\"font-size:16px;line-height:150%;\">≥5%</span><span style=\"font-size:16px;line-height:150%;font-family:宋体;\">）。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<b><span style=\"font-size:12.0pt;line-height:150%;\">5. </span></b><b><span style=\"font-size:12.0pt;line-height:150%;font-family:宋体;\">主要结论</span></b><b><span style=\"font-size:12.0pt;line-height:150%;\"></span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:24.0pt;\">\r\n	<span style=\"font-size:12.0pt;line-height:150%;font-family:宋体;\">本实验采用分光光度计测定苜草素中总皂苷的含量，标准曲线的线性关系良好（</span><i><span style=\"font-size:12.0pt;line-height:150%;\">R</span></i><sup><span style=\"font-size:12.0pt;line-height:150%;\">2</span></sup><span style=\"font-size:12.0pt;line-height:150%;\">=1</span><span style=\"font-size:12.0pt;line-height:150%;font-family:宋体;\">），检测</span><span style=\"font-size:12.0pt;line-height:150%;\">4</span><span style=\"font-size:12.0pt;line-height:150%;font-family:宋体;\">份苜草素样品中总皂苷的含量为</span><span style=\"font-size:12.0pt;line-height:150%;\">36.29-41.44 mg/g</span><span style=\"font-size:12.0pt;line-height:150%;font-family:宋体;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:24.0pt;\">\r\n	<span style=\"font-size:12.0pt;line-height:150%;font-family:宋体;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:24.0pt;\">\r\n	<span style=\"font-size:12.0pt;line-height:150%;font-family:宋体;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:10.5pt;line-height:150%;font-family:宋体;\">主要参考文献：</span><span style=\"font-size:10.5pt;line-height:150%;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:10.5pt;line-height:150%;\">[1] </span><span style=\"font-size:10.5pt;line-height:150%;font-family:宋体;\">杨江涛</span><span style=\"font-size:10.5pt;line-height:150%;\">, </span><span style=\"font-size:10.5pt;line-height:150%;font-family:宋体;\">董晓芳</span><span style=\"font-size:10.5pt;line-height:150%;\">, </span><span style=\"font-size:10.5pt;line-height:150%;font-family:宋体;\">佟建明</span><span style=\"font-size:10.5pt;line-height:150%;\">, </span><span style=\"font-size:10.5pt;line-height:150%;font-family:宋体;\">刘晴雪</span><span style=\"font-size:10.5pt;line-height:150%;\">, </span><span style=\"font-size:10.5pt;line-height:150%;font-family:宋体;\">张琪</span><span style=\"font-size:10.5pt;line-height:150%;\">, </span><span style=\"font-size:10.5pt;line-height:150%;font-family:宋体;\">吴莹莹</span><span style=\"font-size:10.5pt;line-height:150%;\">. </span><span style=\"font-size:10.5pt;line-height:150%;font-family:宋体;\">苜草素多糖、黄酮和皂苷对小鼠巨噬细胞</span><span style=\"font-size:10.5pt;line-height:150%;\">RAW264. 7B -</span><span style=\"font-size:10.5pt;line-height:150%;font-family:宋体;\">防御素基因表达的影响</span><span style=\"font-size:10.5pt;line-height:150%;\">. </span><span style=\"font-size:10.5pt;line-height:150%;font-family:宋体;\">畜牧兽医学报</span><span style=\"font-size:10.5pt;line-height:150%;\">, 2010, 41( 5) : 608 – 614.</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:right;text-indent:210pt;\">\r\n	<span style=\"font-size:12.0pt;line-height:150%;font-family:宋体;\">检测操作人：</span><u><span style=\"font-size:12.0pt;line-height:150%;\">&nbsp;&nbsp;&nbsp; </span></u><u><span style=\"font-size:12.0pt;line-height:150%;font-family:宋体;\">焦晓林</span></u><u><span style=\"font-size:12.0pt;line-height:150%;\">&nbsp;&nbsp; </span></u><span style=\"font-size:12.0pt;line-height:150%;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:right;text-indent:210pt;\">\r\n	<span style=\"font-size:12.0pt;line-height:150%;font-family:宋体;\">检测负责人：</span><u><span style=\"font-size:12.0pt;line-height:150%;\">&nbsp;&nbsp;&nbsp; </span></u><u><span style=\"font-size:12.0pt;line-height:150%;font-family:宋体;\">高微微</span></u><u><span style=\"font-size:12.0pt;line-height:150%;\">&nbsp;&nbsp;&nbsp; </span></u> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:right;text-indent:210pt;\">\r\n	<span style=\"font-size:12.0pt;line-height:150%;font-family:宋体;\">报告日期：</span><span style=\"font-size:12.0pt;line-height:150%;\">&nbsp; <u>2014</u></span><u><span style=\"font-size:12.0pt;line-height:150%;font-family:宋体;\">年</span></u><u><span style=\"font-size:12.0pt;line-height:150%;\">7</span></u><u><span style=\"font-size:12.0pt;line-height:150%;font-family:宋体;\">月</span></u><u><span style=\"font-size:12.0pt;line-h